export async function test_get_comment_by_valid_id(connection: api.IConnection): Promise<void> {
  // 1. Create a new user (comment author)
  const userInput: IUser.ICreate = {
    user_role_id: typia.random<string>(), // Assume role exists in test db
    username: `e2e_commenter_${Math.random().toString(36).substring(2)}`,
    email: `test_commenter_${Date.now()}@example.com`,
    password: "Test1234!",
    status: "active",
    bio: "Test commenter for comment retrieval"
  };
  const user: IUser = await api.functional.core.users.post(connection, { body: userInput });
  typia.assert<IUser>(user);

  // 2. Create a post for the above user
  const postInput: IPost.ICreate = {
    category_id: typia.random<string>(), // No category API provided; assume exists for E2E
    title: "Comment E2E: Post for Comment Detail Test",
    content: "Post content to be used in comment detail test.",
    user_id: user.id
  };
  const post: IPost = await api.functional.posts.posts.post(connection, { body: postInput });
  typia.assert<IPost>(post);

  // 3. Create a comment on that post
  const commentInput: IComment.ICreate = {
    user_id: user.id,
    post_id: post.id,
    content: "This is a comment for the detail retrieval test.",
  };
  const comment: IComment = await api.functional.posts.comments.post(connection, { body: commentInput });
  typia.assert<IComment>(comment);

  // 4. Fetch the newly-created comment by its ID
  const fetched: IComment = await api.functional.posts.comments.getById(connection, { id: comment.id });
  typia.assert<IComment>(fetched);

  // 5. Validate all important fields match
  TestValidator.equals("comment.id")(comment.id)(fetched.id);
  TestValidator.equals("comment.content")(comment.content)(fetched.content);
  TestValidator.equals("comment.user_id")(comment.user_id)(fetched.user_id);
  TestValidator.equals("comment.post_id")(comment.post_id)(fetched.post_id);
  TestValidator.equals("created_at type")(typeof fetched.created_at)("string");

  // 6. Edge/null checks
  if (typeof comment.parent_id !== "undefined") {
    TestValidator.equals("parent_id")(comment.parent_id)(fetched.parent_id);
  } else {
    TestValidator.equals("parent_id is undefined or null")(comment.parent_id ?? null)(fetched.parent_id ?? null);
  }
  if (typeof comment.deleted_at !== "undefined") {
    TestValidator.equals("deleted_at")(comment.deleted_at)(fetched.deleted_at);
  } else {
    TestValidator.equals("deleted_at is undefined or null")(comment.deleted_at ?? null)(fetched.deleted_at ?? null);
  }
}

