  // Step 1: Create valid user (comment author)
  const user: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: typia.random<IUser.ICreate>().user_role_id, // must be a valid role
      username: `commenter_${Math.random().toString(36).substring(2, 10)}`,
      email: `commenter_${Math.random().toString(36).substring(2, 10)}@test.com`,
      password: "StrongPa33w0rd!",
      status: "active",
      bio: "Test comment author"
    },
  });
  typia.assert<IUser>(user);

  // Step 2: Create valid post (so that post_id exists)
  const post: IPost = await api.functional.posts.posts.post(connection, {
    body: {
      category_id: typia.random<IPost.ICreate>().category_id, // just pick a random category uuid
      title: "Unit Test Post for Missing Comment Fields",
      content: "This post is used for negative comment test cases.",
      user_id: user.id,
    },
  });
  typia.assert<IPost>(post);

  // Step 3: Prepare invalid request bodies missing various required fields
  const makeValidCommentInput = (): IComment.ICreate => ({
    user_id: user.id,
    post_id: post.id,
    content: "This is a valid comment."
  });

  const invalidBodies: { title: string; body: Partial<IComment.ICreate> }[] = [
    {
      title: "missing content",
      body: {
        user_id: user.id,
        post_id: post.id,
        // content: missing
      },
    },
