      title: "missing post_id",
      body: {
        user_id: user.id,
        content: "Missing post ID!",
        // post_id: missing
      },
    },
    {
      title: "missing user_id",
      body: {
        post_id: post.id,
        content: "Missing user ID!",
        // user_id: missing
      },
    },
    {
      title: "missing all required fields",
      body: {
        // user_id: missing
        // post_id: missing
        // content: missing
      },
    },
  ];

  // Step 4: For each invalid input, test that API returns 400 Bad Request (validation error)
  for (const testCase of invalidBodies) {
    await TestValidator.httpError(`should fail with 400 when ${testCase.title}`)(400)(async () =>
      api.functional.posts.comments.post(connection, {
        // as any => let the API handler trigger validation error
        body: testCase.body as any
      })
    );
  }

  // Step 5: Positive control - ensure normal creation works
  const validInput = makeValidCommentInput();
  const created: IComment = await api.functional.posts.comments.post(connection, {
    body: validInput,
  });
  typia.assert<IComment>(created);
