    validInput.content
  )(created.content);
  TestValidator.equals("correctly associated to post")(
    validInput.post_id
  )(created.post_id);
  TestValidator.equals("correctly associated user")(
    validInput.user_id
  )(created.user_id);
}

import { TestValidator } from "@nestia/e2e";
