import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IComment";
import typia from "typia";

export async function test_create_comment_with_non_existent_post(
  connection: api.IConnection,
): Promise<void> {
  // 1. Register a valid user
  const user: IUser = await api.functional.core.users.post(connection, {
    body: typia.random<IUser.ICreate>(),
  });

  // 2. Use a clearly fake/non-existent post UUID
  const NON_EXISTENT_POST_ID = "11111111-1111-1111-1111-111111111111";

  // 3. Construct an invalid comment creation payload
  const commentInput: IComment.ICreate = {
    user_id: user.id,
    post_id: NON_EXISTENT_POST_ID,
    content: "This should not succeed.",
  };

  // 4 & 5. Attempt to create the comment, expect error (400/404)
  await TestValidator.httpError("Should be 400 or 404 for non-existent post")
    (400, 404)(
      () => api.functional.posts.comments.post(connection, { body: commentInput })
    );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import { IComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IComment";
import typia from "typia";

/**
 * Update an existing comment's content and verify the update.
 */
export async function test_update_comment_with_valid_data(connection: api.IConnection): Promise<void> {
  // 1. Create a role ID for user creation.
  // (For test, use random UUID as role_id, must exist in test DB or be adjusted as required)
  const userRoleId: string = typia.random<IUser["user_role_id"]>();

  // 2. Create user
  const userInput: IUser.ICreate = {
    user_role_id: userRoleId,
    username: `testuser_${Date.now()}`,
    email: `user_${Date.now()}@example.com`,
    bio: "Test user for comment update",
    password: "securePassword123!",
    status: "active",
  };
  const user: IUser = await api.functional.core.users.post(connection, { body: userInput });
  typia.assert<IUser>(user);

  // 3. Create post owned by test user (Provide actual category_id required by your system)
  const categoryId: string = typia.random<IPost["category_id"]>();
  const postInput: IPost.ICreate = {
    category_id: categoryId,
    title: `Test post for comment update ${Date.now()}`,
    content: "Initial test post content.",
    user_id: user.id,
  };
  const post: IPost = await api.functional.posts.posts.post(connection, { body: postInput });
  typia.assert<IPost>(post);

  // 4. Create comment on the post by test user
  const commentInput: IComment.ICreate = {
    user_id: user.id,
    post_id: post.id,
    content: "Original comment content.",
  };
  const comment: IComment = await api.functional.posts.comments.post(connection, { body: commentInput });
  typia.assert<IComment>(comment);

  // 5. Prepare new content for update
  const newContent = `Updated comment content at ${Date.now()}`;
  const commentUpdate: IComment.IUpdate = {
    content: newContent,
  };

  // 6. Update comment by id
  const updated: IComment = await api.functional.posts.comments.putById(connection, {
    id: comment.id,
    body: commentUpdate,
  });
  typia.assert<IComment>(updated);

  // 7. Validations
  // a. The ID must match
  TestValidator.equals("Comment ID should match.")(comment.id)(updated.id);
  // b. Content must be updated
  TestValidator.equals("Content should be updated.")(newContent)(updated.content);
  // c. The user_id and post_id remain the same
  TestValidator.equals("User ID should remain unchanged.")(comment.user_id)(updated.user_id);
  TestValidator.equals("Post ID should remain unchanged.")(comment.post_id)(updated.post_id);
  // d. Parent_id stays the same
  TestValidator.equals("Parent ID should remain unchanged.")(comment.parent_id ?? null)(updated.parent_id ?? null);

  // e. created_at doesn't change
  TestValidator.equals("Created time should remain unchanged.")(comment.created_at)(updated.created_at);

  // f. updated_at changes
  TestValidator.predicate("Update time should be newer.")(() =>
    new Date(updated.updated_at).getTime() >= new Date(comment.updated_at).getTime()
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
