import { IComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IComment";

// Attempts to update a comment with invalid/missing fields,
// and validates that appropriate validation errors are returned.
export async function test_update_comment_with_invalid_fields(
  connection: api.IConnection,
): Promise<void> {
  // 1. Register a new user
  const user: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: typia.random<string & tags.Format<"uuid">>(),
      username: `e2e_comment_user_${Date.now()}`,
      email: `comment_test_${Date.now()}@example.com`,
      password: "TestPassword1!",
      status: "active",
    },
  });
  typia.assert<IUser>(user);

  // 2. Create a post
  const post: IPost = await api.functional.posts.posts.post(connection, {
    body: {
      category_id: typia.random<string & tags.Format<"uuid">>(),
      title: "Test comment validation post",
      content: "Test post body.",
      user_id: user.id,
    },
  });
  typia.assert<IPost>(post);

  // 3. Create a comment
  const comment: IComment = await api.functional.posts.comments.post(
    connection,
    {
      body: {
        user_id: user.id,
        post_id: post.id,
        content: "A valid comment before attempting invalid update.",
      },
    },
  );
  typia.assert<IComment>(comment);

  // 4. Attempt to update with empty content (should fail validation)
  await TestValidator.httpError("update comment: empty content should fail")(400)(async () =>
    api.functional.posts.comments.putById(connection, {
      id: comment.id,
      body: {
        content: "",
      },
    })
  );

  // 5. Attempt to update with missing content entirely (should fail validation)
  await TestValidator.httpError("update comment: missing content should fail")(400)(async () =>
    api.functional.posts.comments.putById(connection, {
      id: comment.id,
      body: {},
    })
  );
}

