  const comment: IComment = await api.functional.posts.comments.post(connection, {
    body: {
      ...typia.random<IComment.ICreate>(),
      user_id: user.id,
      post_id: post.id,
    },
  });
  typia.assert<IComment>(comment);

  // 4. Soft-delete the comment
  const deleteResult: IComment.IDeleteResult = await api.functional.posts.comments.eraseById(connection, { id: comment.id });
  typia.assert<IComment.IDeleteResult>(deleteResult);
  TestValidator.equals("eraseById: deleted true")<boolean>(true)(deleteResult.deleted);
  TestValidator.equals("eraseById: id matches")<string>(comment.id)(deleteResult.id);

  // 5. (Optional: If comment fetch-by-ID API exists) Fetch the comment again and check deleted_at is set
  // (Assume such a GET API exists - if not, this block can be skipped)
  // try {
  //   const fetched: IComment = await api.functional.posts.comments.getById(connection, { id: comment.id });
  //   typia.assert<IComment>(fetched);
  //   TestValidator.predicate("deleted_at is non-null")(() => !!fetched.deleted_at);
  //   TestValidator.equals("user_id unchanged")(comment.user_id)(fetched.user_id);
  //   TestValidator.equals("post_id unchanged")(comment.post_id)(fetched.post_id);
  //   TestValidator.equals("content unchanged")(comment.content)(fetched.content);
  // } catch {
  //   // If API to fetch single soft-deleted comments rejects, skip
  // }

  // 6. [Edge] Try soft-deleting again and assert error or deleted=true
  await TestValidator.error("double delete returns error or is idempotent")(
    async () => {
      await api.functional.posts.comments.eraseById(connection, { id: comment.id });
    },
  );
}

