import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import { IVote } from "@ORGANIZATION/PROJECT-api/lib/structures/IVote";
import { IPageIVote } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIVote";
import typia from "typia";

export async function test_list_votes_with_pagination_and_filters(connection: api.IConnection): Promise<void> {
  // 1. Create users
  const userA: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: typia.random<string>(),
      username: "filterUserA_" + Math.random().toString(36).substring(2, 10),
      email: `userA_${Math.random().toString(36).substring(2,10)}@test.com`,
      password: "passA123!",
      status: "active",
    },
  });
  const userB: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: userA.user_role_id, // Use same role for both
      username: "filterUserB_" + Math.random().toString(36).substring(2, 10),
      email: `userB_${Math.random().toString(36).substring(2,10)}@test.com`,
      password: "passB123!",
      status: "active",
    },
  });

  // 2. Create posts
  const post1: IPost = await api.functional.posts.posts.post(connection, {
    body: {
      category_id: typia.random<string>(),
      title: "Vote Test Post 1",
      content: "Testing vote filtering 1.",
      user_id: userA.id,
    },
  });
  const post2: IPost = await api.functional.posts.posts.post(connection, {
    body: {
      category_id: post1.category_id,
      title: "Vote Test Post 2",
      content: "Testing vote filtering 2.",
      user_id: userB.id,
    },
  });

  // 3. Cast votes
  const votes: IVote[] = [];
  votes.push(
    await api.functional.votes.votes.post(connection, {
      body: { user_id: userA.id, value: 1, post_id: post1.id },
    })
  );
  votes.push(
    await api.functional.votes.votes.post(connection, {
      body: { user_id: userA.id, value: 1, post_id: post2.id },
    })
  );
  votes.push(
    await api.functional.votes.votes.post(connection, {
      body: { user_id: userB.id, value: -1, post_id: post1.id },
    })
  );
  votes.push(
    await api.functional.votes.votes.post(connection, {
      body: { user_id: userB.id, value: 1, post_id: post2.id },
    })
  );

  // 4. Retrieve votes filtered by userId=userA.id
  const filteredByUserA: IPageIVote = await api.functional.votes.votes.patch(connection, {
    body: { userId: userA.id },
  });
  typia.assert<IPageIVote>(filteredByUserA);
  TestValidator.predicate("votes filtered by userId=userA.id have only userA")( () =>
    filteredByUserA.data.every((v) => v.user_id === userA.id)
  );

  // 5. Retrieve votes filtered by postId=post1.id
  const filteredByPost1: IPageIVote = await api.functional.votes.votes.patch(connection, {
    body: { postId: post1.id },
  });
  typia.assert<IPageIVote>(filteredByPost1);
  TestValidator.predicate("votes filtered by postId=post1.id reference only post1")( () =>
    filteredByPost1.data.every((v) => v.post_id === post1.id)
  );

  // 6. Retrieve votes (no filters, limit=2, sort by value desc)
  const pagedSorted: IPageIVote = await api.functional.votes.votes.patch(connection, {
    body: { limit: 2, sort: "value:desc" },
  });
  typia.assert<IPageIVote>(pagedSorted);
  TestValidator.predicate("paging + sorting returns <= limit")(
    () => pagedSorted.data.length <= 2
  );
  TestValidator.predicate("paging by value:desc sorted order")(
    () => pagedSorted.data.every((v, i, arr) => i === 0 || arr[i-1].value >= v.value)
  );

  // 7. Retrieve votes offset=1, limit=2 and check pagination
  const offsetPaging: IPageIVote = await api.functional.votes.votes.patch(connection, {
    body: { offset: 1, limit: 2 },
  });
  typia.assert<IPageIVote>(offsetPaging);
  TestValidator.predicate("offset paging returns <= limit")(
    () => offsetPaging.data.length <= 2
  );
  TestValidator.predicate("pagination meta present")(
    () => typeof offsetPaging.pagination === 'object' && offsetPaging.pagination.limit === 2
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import { IVote } from "@ORGANIZATION/PROJECT-api/lib/structures/IVote";
import typia from "typia";

export async function test_get_vote_by_valid_id(connection: api.IConnection): Promise<void> {
  // 1. Create a user (voter)
  const userInput: IUser.ICreate = {
    user_role_id: typia.random<string & typia.tags.Format<"uuid">>(),
    username: `vote_test_user_${Math.floor(Math.random() * 100000)}`,
    email: `vote_test_user_${Math.floor(Math.random() * 100000)}@example.com`,
    bio: "Voter for test vote.",
    password: "SecureP@ssw0rd!",
    status: "active",
  };
  const user: IUser = await api.functional.core.users.post(connection, { body: userInput });
  typia.assert<IUser>(user);

  // 2. Create a post by the user
  const postInput: IPost.ICreate = {
    category_id: typia.random<string & typia.tags.Format<"uuid">>(),
    title: `Test Vote Target Post ${Math.floor(Math.random() * 100000)}`,
    content: "Testing post content for vote retrieval scenario.",
    user_id: user.id,
  };
  const post: IPost = await api.functional.posts.posts.post(connection, { body: postInput });
  typia.assert<IPost>(post);

  // 3. Cast a vote (on post by user)
  const voteInput: IVote.ICreate = {
    user_id: user.id,
    value: 1, // upvote (+1). Could randomize +1/-1 for robustness.
    post_id: post.id,
  };
  const vote: IVote = await api.functional.votes.votes.post(connection, { body: voteInput });
  typia.assert<IVote>(vote);

  // 4. Retrieve the vote by id
  const found: IVote = await api.functional.votes.votes.getById(connection, { id: vote.id });
  typia.assert<IVote>(found);

  // 5. Assert all required fields match original
  TestValidator.equals("vote.id")(vote.id)(found.id);
  TestValidator.equals("vote.user_id")(vote.user_id)(found.user_id);
  TestValidator.equals("vote.value")(vote.value)(found.value);
  TestValidator.equals("vote.post_id")(vote.post_id)(found.post_id);
  TestValidator.equals("vote.comment_id")(vote.comment_id ?? null)(found.comment_id ?? null);
  // Timestamps may slightly differ if system updates milliseconds; allow exact or close (check only ISO format)
  TestValidator.predicate("vote.created_at format")(() => typeof found.created_at === "string" && found.created_at.length > 10);
}

