import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import { IVote } from "@ORGANIZATION/PROJECT-api/lib/structures/IVote";

export async function test_update_vote_with_valid_data(connection: api.IConnection): Promise<void> {
  // 1. Register user
  const userInput: IUser.ICreate = {
    user_role_id: typia.random<string>(), // Should be a valid UUID for an existing role
    username: `testuser_${Date.now()}`,
    email: `user${Date.now()}@example.com`,
    password: "TestPassword123!",
    status: "active",
  };
  const user: IUser = await api.functional.core.users.post(connection, { body: userInput });
  typia.assert(user);

  // 2. Create post
  const postInput: IPost.ICreate = {
    category_id: typia.random<string>(), // Should be a valid UUID for an existing category
    title: `Test post ${Date.now()}`,
    content: "Sample content for vote update test.",
    user_id: user.id,
  };
  const post: IPost = await api.functional.posts.posts.post(connection, { body: postInput });
  typia.assert(post);

  // 3. Cast initial vote
  const initialVoteInput: IVote.ICreate = {
    user_id: user.id,
    value: +1,
    post_id: post.id,
    comment_id: null,
  };
  const vote: IVote = await api.functional.votes.votes.post(connection, { body: initialVoteInput });
  typia.assert(vote);
  TestValidator.equals("initial vote matches input")<number>(initialVoteInput.value)(vote.value);
  TestValidator.equals("vote user unchanged")<string>(initialVoteInput.user_id)(vote.user_id);
  TestValidator.equals("vote post unchanged")<string | null>(initialVoteInput.post_id ?? null)(vote.post_id ?? null);

  // 4. Update the vote value (flip +1 to -1)
  const updateInput: IVote.IUpdate = {
    value: -1,
  };
  const updated: IVote = await api.functional.votes.votes.putById(connection, { id: vote.id, body: updateInput });
  typia.assert(updated);

  // 5. Validate updated vote reflects new value but other fields stay the same
  TestValidator.equals("vote value updated")<number>(updateInput.value)(updated.value);
  TestValidator.equals("user_id immutable")<string>(vote.user_id)(updated.user_id);
  TestValidator.equals("post_id immutable")<string | null>(vote.post_id ?? null)(updated.post_id ?? null);
  TestValidator.equals("comment_id immutable")<string | null>(vote.comment_id ?? null)(updated.comment_id ?? null);
  TestValidator.equals("vote id same")<string>(vote.id)(updated.id);
}

