test/features/api/user/test_list_users_w:
import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPageIUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIUser";

export async function test_list_users_with_various_filters_and_pagination(connection: api.IConnection): Promise<void> {
  // 1. No filters (default pagination)
  const resDefault = await api.functional.core.users.patch(connection, {
    body: {},
  });
  typia.assert<IPageIUser.ISummary>(resDefault);
  TestValidator.predicate("default result data is array")(() => Array.isArray(resDefault.data));
  TestValidator.predicate("default pagination current >= 1")(() => resDefault.pagination.current >= 1);

  // 2. Specific page and limit
  const resPage2 = await api.functional.core.users.patch(connection, {
    body: { page: 2, limit: 1 },
  });
  typia.assert<IPageIUser.ISummary>(resPage2);
  TestValidator.equals("pagination current page", undefined)(resPage2.pagination.current)(2);
  TestValidator.equals("pagination limit", undefined)(resPage2.pagination.limit)(1);
  TestValidator.predicate("data is array for page 2, limit 1")(() => Array.isArray(resPage2.data));

  // 3. Filter by fake username (should return empty data)
  const resNotFound = await api.functional.core.users.patch(connection, {
    body: { search: "this_user_does_not_exist_123!@#" },
  });
  typia.assert<IPageIUser.ISummary>(resNotFound);
  TestValidator.equals("no result for impossible search")([] as IUser.ISummary[])(resNotFound.data);

  // 4. Filter by random/unknown role (should return empty result)
  const resFakeRole = await api.functional.core.users.patch(connection, {
    body: { role: "00000000-0000-0000-0000-000000000000" },
  });
  typia.assert<IPageIUser.ISummary>(resFakeRole);
  TestValidator.equals("no result for fake role")([] as IUser.ISummary[])(resFakeRole.data);

  // 5. Filter by impossible status (should return empty)
  const resFakeStatus = await api.functional.core.users.patch(connection, {
    body: { status: "_____totallyFakeStatus_____" },
  });
  typia.assert<IPageIUser.ISummary>(resFakeStatus);
  TestValidator.equals("no result for impossible status")([] as IUser.ISummary[])(resFakeStatus.data);

  // 6. Invalid negative page value (should be handled with error or fallback)
  await TestValidator.httpError("negative page handled")(400)(async () => {
    await api.functional.core.users.patch(connection, {
      body: { page: -1 },
    });
  });
}

test/features/api/user/test_search_users:
import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IUserRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserRole";
import { IPageIUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIUser";
import typia from "typia";

export async function test_search_users_with_role_and_keyword_filters(connection: api.IConnection): Promise<void> {
  // 1. Create roles
  const memberRole: IUserRole = await api.functional.core.userRoles.post(connection, {
    body: { name: "Member", description: "Standard member role" }
  });
  const moderatorRole: IUserRole = await api.functional.core.userRoles.post(connection, {
    body: { name: "Moderator", description: "Moderator role" }
  });

  // 2. Create users
  // Member: 'alice', 'bob', Moderator: 'alicemod', 'charlie'
  const userMemberAlice: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: memberRole.id,
      username: "alice",
      email: "alice@example.com",
      password: "Password1!",
      status: "active"
    }
  });
  const userMemberBob: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: memberRole.id,
      username: "bob",
      email: "bob@example.com",
      password: "Password1!",
      status: "active"
    }
  });
  const userModAlicemod: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: moderatorRole.id,
      username: "alicemod",
      email: "alicemod@example.com",
      password: "Password1!",
      status: "active"
    }
  });
  const userModCharlie: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: moderatorRole.id,
      username: "charlie",
      email: "charlie@example.com",
      password: "Password1!",
      status: "active"
    }
  });

  // 3. Filter by role: should return only memberRole's users
  const byRole: IPageIUser.ISummary = await api.functional.core.users.patch(connection, {
    body: { role: memberRole.id }
  });
  typia.assert<IPageIUser.ISummary>(byRole);
  TestValidator.predicate("All users are members")(byRole.data.every(u => u.role.id === memberRole.id));
  TestValidator.equals("Only alice and bob returned by role")([
    userMemberAlice.username,
    userMemberBob.username
  ].sort())(byRole.data.map(u => u.username).sort());

  // 4. Search by partial username (ali): should get 'alice', 'alicemod'
  const byPartial: IPageIUser.ISummary = await api.functional.core.users.patch(connection, {
    body: { search: "ali" }
  });
  typia.assert<IPageIUser.ISummary>(byPartial);
  const expectedAli = [userMemberAlice.username, userModAlicemod.username].sort();
  TestValidator.equals("Search by 'ali'")(expectedAli)(byPartial.data.map(u => u.username).filter(x => x.includes("ali")).sort());

  // 5. Combined: role=Member and search='ali' (should return only 'alice')
  const byRoleAndPartial: IPageIUser.ISummary = await api.functional.core.users.patch(connection, {
    body: { role: memberRole.id, search: "ali" }
  });
  typia.assert<IPageIUser.ISummary>(byRoleAndPartial);
  TestValidator.equals("Role member and 'ali' only alice")(["alice"])(byRoleAndPartial.data.map(u => u.username));

  // 6a. Edge: search by a role that does not exist â€“ expect empty result
  const nonexistentUUID = "12345678-aaaa-bbbb-cccc-123456789abc";
  const byNonexistentRole: IPageIUser.ISummary = await api.functional.core.users.patch(connection, {
    body: { role: nonexistentUUID }
  });
  typia.assert<IPageIUser.ISummary>(byNonexistentRole);
  TestValidator.equals("Role does not exist returns empty")([] as string[])(byNonexistentRole.data.map(u => u.username));

  // 6b. Edge: search keyword that matches nobody ("zzz")
  const byNoKeyword: IPageIUser.ISummary = await api.functional.core.users.patch(connection, {
    body: { search: "zzz" }
  });
  typia.assert<IPageIUser.ISummary>(byNoKeyword);
  TestValidator.equals("No keyword match returns empty")([] as string[])(byNoKeyword.data.map(u => u.username));
}

test/features/api/user/test_retrieve_use:
import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import typia, { tags } from "typia";

export async function test_retrieve_user_profile_by_id_success_and_not_found(
  connection: api.IConnection,
): Promise<void> {
  // 1. Register a new user to get a valid user ID
  const userInput: IUser.ICreate = {
    user_role_id: typia.random<string & tags.Format<"uuid">>(),
    username: `testuser_${Math.floor(Math.random() * 100000)}`,
    email: `testuser_${Math.floor(Math.random() * 100000)}@test.com`,
    password: "TestPassword123!",
    status: "active",
    bio: "Test bio text.",
  };
  const createdUser: IUser = await api.functional.core.users.post(connection, {
    body: userInput,
  });
  typia.assert<IUser>(createdUser);

  // 2. Retrieve user profile by ID (success)
  const fetchedUser: IUser = await api.functional.core.users.getById(connection, {
    id: createdUser.id,
  });
  typia.assert<IUser>(fetchedUser);

  // 3. Validate user fields match registration input
  TestValidator.equals("user id")(createdUser.id)(fetchedUser.id);
  TestValidator.equals("username")(createdUser.username)(fetchedUser.username);
  TestValidator.equals("email")(createdUser.email)(fetchedUser.email);
  TestValidator.equals("user_role_id")(createdUser.user_role_id)(fetchedUser.user_role_id);
  TestValidator.equals("bio")(createdUser.bio)(fetchedUser.bio);
  TestValidator.equals("status")(createdUser.status)(fetchedUser.status);

  // 4. Attempt to retrieve non-existent user ID (404 expected)
  const randomUserId: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("User not found should return 404")(404)(async () => {
    await api.functional.core.users.getById(connection, { id: randomUserId });
  });
}

