test/features/api/user-role/test_list_us:
import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IUserRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IUserRole";
import { IPageIUserRole } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIUserRole";

export async function test_list_user_roles_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create roles with diverse data.
  const roleInputs: IUserRole.ICreate[] = [
    { name: "Moderator", description: "Can moderate content." },
    { name: "Administrator", description: "Full system privileges." },
    { name: "Member", description: "Standard member with limited permissions." },
    { name: "Editor", description: "Can edit articles but not moderate." },
    { name: "Guest", description: "Read-only access." },
  ];
  const createdRoles: IUserRole[] = [];
  for (const input of roleInputs) {
    const created = await api.functional.core.userRoles.post(connection, { body: input });
    typia.assert<IUserRole>(created);
    createdRoles.push(created);
  }

  // 2. List all roles (no filter), expect all created roles present
  {
    const page = await api.functional.core.userRoles.patch(connection, { body: {} });
    typia.assert<IPageIUserRole>(page);
    // The minimum expectation: all created names should be present at least once
    const listedNames = page.data.map(r => r.name);
    for (const input of roleInputs) {
      TestValidator.predicate(`Role '${input.name}' should be listed`)(() => listedNames.includes(input.name));
    }
    // No duplicates
    const nameSet = new Set(listedNames);
    TestValidator.equals("no duplicates among listed roles")(nameSet.size)(listedNames.length);
  }

  // 3. Filter by partial role name
  {
    const searchKeyword = "Mod";
    const page = await api.functional.core.userRoles.patch(connection, { body: { search: searchKeyword } });
    typia.assert<IPageIUserRole>(page);
    // Should only include 'Moderator'
    TestValidator.predicate("Expect role names to match search 'Mod'")(
      () => page.data.every(r => r.name.includes(searchKeyword)),
    );
  }

  // 4. Filter by description keyword
  {
    const searchKeyword = "edit"; // should match "Editor" and description of Editor
    const page = await api.functional.core.userRoles.patch(connection, { body: { search: searchKeyword } });
    typia.assert<IPageIUserRole>(page);
    TestValidator.predicate("Description keyword filter 'edit'")(
      () => page.data.some(r => r.name === "Editor"),
    );
  }

  // 5. Negative: Search that yields zero results
  {
    const page = await api.functional.core.userRoles.patch(connection, { body: { search: "NonExistentRole" } });
    typia.assert<IPageIUserRole>(page);
    TestValidator.equals("Search yielding no results")(0)(page.data.length);
  }

  // 6. Pagination correctness (limit=2, page=2)
  {
    const limit = 2;
    const pageNum = 2;
    const page = await api.functional.core.userRoles.patch(connection, { body: { limit, page: pageNum } });
    typia.assert<IPageIUserRole>(page);
    // Expect limit or less results
    TestValidator.predicate("Page size limit respected")(
      () => page.data.length <= limit,
    );
    // Check page metadata
    TestValidator.equals("pagination current page")(pageNum)(page.pagination.current);
    TestValidator.equals("pagination limit")(limit)(page.pagination.limit);
    // No duplicates within this page
    const namesInPage = page.data.map(r => r.name);
    const nameSet = new Set(namesInPage);
    TestValidator.equals("no duplicates in paged data")(nameSet.size)(namesInPage.length);
  }
}

