import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { ICategory } from "@ORGANIZATION/PROJECT-api/lib/structures/ICategory";
import { IPageICategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICategory";

export async function test_list_categories_with_pagination_and_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Prepare: Create 5 test categories with different names/descriptions
  const categories: ICategory[] = [];
  for (let i = 0; i < 5; i++) {
    const name = `test-cat-${i}`;
    const description = `description tag ${i % 2 === 0 ? "even" : "odd"}`;
    const created = await api.functional.core.categories.post(connection, {
      body: { name, description },
    });
    typia.assert<ICategory>(created);
    categories.push(created);
  }

  // 2. Test Pagination: limit = 2, page = 1
  const pageReq: ICategory.IRequest = { page: 1, limit: 2 };
  const paged1 = await api.functional.core.categories.patch(connection, {
    body: pageReq,
  });
  typia.assert<IPageICategory>(paged1);
  TestValidator.equals("pagination current page")(1)(paged1.pagination.current);
  TestValidator.equals("pagination limit")(2)(paged1.pagination.limit);
  TestValidator.equals("data length")(2)(paged1.data.length);

  // Next page
  const paged2 = await api.functional.core.categories.patch(connection, {
    body: { ...pageReq, page: 2 },
  });
  typia.assert<IPageICategory>(paged2);
  TestValidator.equals("pagination current page")(2)(paged2.pagination.current);
  TestValidator.equals("data length")(2)(paged2.data.length);

  // 3. Test Filtering by name (exact)
  const filterName = categories[1].name;
  const filteredByName = await api.functional.core.categories.patch(connection, {
    body: { page: 1, limit: 10, name: filterName },
  });
  typia.assert<IPageICategory>(filteredByName);
  TestValidator.equals("filtered name all match")(true)(
    filteredByName.data.every((cat) => cat.name === filterName),
  );

  // 4. Test Filtering by description (substring)
  const filterDesc = "even";
  const filteredByDesc = await api.functional.core.categories.patch(connection, {
    body: { page: 1, limit: 10, description: filterDesc },
  });
  typia.assert<IPageICategory>(filteredByDesc);
  TestValidator.equals("filtered desc contains")(true)(
    filteredByDesc.data.every((cat) => cat.description.includes(filterDesc)),
  );

  // 5. Edge Case: limit = 1
  const lim1Resp = await api.functional.core.categories.patch(connection, {
    body: { page: 1, limit: 1 },
  });
  typia.assert<IPageICategory>(lim1Resp);
  TestValidator.equals("pagination limit-1")(1)(lim1Resp.pagination.limit);
  TestValidator.equals("data length limit-1")(1)(lim1Resp.data.length);

  // 6. Edge Case: out-of-range page (should return empty data array)
  const bigPageResp = await api.functional.core.categories.patch(connection, {
    body: { page: 100, limit: 10 },
  });
  typia.assert<IPageICategory>(bigPageResp);
  TestValidator.equals("empty data for non-existent page")(
    [] as ICategory[],
  )(bigPageResp.data);

  // 7. Edge Case: no matching filter (name doesn't exist)
  const noMatchResp = await api.functional.core.categories.patch(connection, {
    body: { page: 1, limit: 10, name: "__zz_NOPE619312__" },
  });
  typia.assert<IPageICategory>(noMatchResp);
  TestValidator.equals("empty data for filter miss")(
    [] as ICategory[],
  )(noMatchResp.data);
}

