test/features/api/post/test_list_posts_w:
import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { ICategory } from "@ORGANIZATION/PROJECT-api/lib/structures/ICategory";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import { IPageIPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPost";

export async function test_list_posts_with_pagination_and_filters(connection: api.IConnection): Promise<void> {
  // 1. Create a user
  const userInput: IUser.ICreate = {
    user_role_id: "11111111-1111-1111-1111-111111111111", // Replace with a valid user_role_id as needed
    username: `author_${Math.random().toString(36).substring(2, 8)}`,
    email: `author_${Date.now()}@test.com`,
    bio: "Test author bio",
    password: "password1234!",
    status: "active",
  };
  const user: IUser = await api.functional.core.users.post(connection, { body: userInput });
  typia.assert<IUser>(user);

  // 2. Create a category
  const categoryInput: ICategory.ICreate = {
    name: `Category_${Math.random().toString(36).substring(2, 9)}`,
    description: "E2E test category description",
  };
  const category: ICategory = await api.functional.core.categories.post(connection, { body: categoryInput });
  typia.assert<ICategory>(category);

  // 3. Create multiple posts
  const posts: IPost[] = [];
  for (let i = 0; i < 3; ++i) {
    const postInput: IPost.ICreate = {
      category_id: category.id,
      title: `Test Post Title ${i + 1}`,
      content: `This is content for post #${i + 1}`,
      user_id: user.id,
    };
    const post: IPost = await api.functional.posts.posts.post(connection, { body: postInput });
    typia.assert<IPost>(post);
    posts.push(post);
  }

  // 4a. List by author id
  let req: IPost.IRequest = { user_id: user.id, page: 1, limit: 10 };
  let page: IPageIPost = await api.functional.posts.posts.patch(connection, { body: req });
  typia.assert<IPageIPost>(page);
  TestValidator.predicate("all by author id")(() => page.data.every((p) => p.user_id === user.id));

  // 4b. List by category id
  req = { category_id: category.id, page: 1, limit: 10 };
  page = await api.functional.posts.posts.patch(connection, { body: req });
  typia.assert<IPageIPost>(page);
  TestValidator.predicate("all by category id")(() => page.data.every((p) => p.category_id === category.id));

  // 4c. By both user_id & category_id
  req = { user_id: user.id, category_id: category.id, page: 1, limit: 10 };
  page = await api.functional.posts.posts.patch(connection, { body: req });
  typia.assert<IPageIPost>(page);
  TestValidator.predicate("all by user and category")(
    () => page.data.every((p) => p.category_id === category.id && p.user_id === user.id),
  );

  // 4d. By title keyword
  req = { title: "Test Post Title 1", page: 1, limit: 10 };
  page = await api.functional.posts.posts.patch(connection, { body: req });
  typia.assert<IPageIPost>(page);
  TestValidator.predicate("all contain title keyword")(
    () => page.data.every((p) => p.title.includes("Test Post Title 1")),
  );

  // 4e. Pagination (limit=2)
  req = { page: 1, limit: 2 };
  page = await api.functional.posts.posts.patch(connection, { body: req });
  typia.assert<IPageIPost>(page);
  TestValidator.equals("limit is two")<number>(2)(page.pagination.limit);
  TestValidator.predicate("at most 2 records")(() => page.data.length <= 2);

  // 4f. Pagination page 2 (limit=1)
  req = { page: 2, limit: 1 };
  page = await api.functional.posts.posts.patch(connection, { body: req });
  typia.assert<IPageIPost>(page);
  TestValidator.equals("current is page 2")<number>(2)(page.pagination.current);

  // 6. Negative tests - random user, category, or title (should be empty data)
  req = { user_id: "00000000-0000-0000-0000-000000000000", page: 1, limit: 10 };
  page = await api.functional.posts.posts.patch(connection, { body: req });
  typia.assert<IPageIPost>(page);
  TestValidator.equals("empty data on non-existing user")<IPost[]>([])(page.data);

  req = { category_id: "00000000-0000-0000-0000-000000000000", page: 1, limit: 10 };
  page = await api.functional.posts.posts.patch(connection, { body: req });
  typia.assert<IPageIPost>(page);
  TestValidator.equals("empty data on non-existing category")<IPost[]>([])(page.data);

  req = { title: "DefinitelyNoSuchTitleKeywordExist", page: 1, limit: 10 };
  page = await api.functional.posts.posts.patch(connection, { body: req });
  typia.assert<IPageIPost>(page);
  TestValidator.equals("empty data on non-match title")<IPost[]>([])(page.data);
}

