import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import { ICategory } from "@ORGANIZATION/PROJECT-api/lib/structures/ICategory";

export async function test_api_posts_getById_with_valid_id(
  connection: api.IConnection
): Promise<void> {
  // 1. Create a user (as author)
  const userInput: IUser.ICreate = {
    user_role_id: typia.random<string & tags.Format<"uuid">>(),
    username: `author_${Date.now()}`,
    email: `author_${Date.now()}@example.com`,
    bio: "Post author for test",
    password: "test-password-1234",
    status: "active",
  };
  const user: IUser = await api.functional.core.users.post(connection, { body: userInput });
  typia.assert<IUser>(user);

  // 2. Create a category
  const catInput: ICategory.ICreate = {
    name: `category_${Date.now()}`,
    description: "E2E Test Category",
  };
  const category: ICategory = await api.functional.core.categories.post(connection, { body: catInput });
  typia.assert<ICategory>(category);

  // 3. Create a post
  const postInput: IPost.ICreate = {
    user_id: user.id,
    category_id: category.id,
    title: `Test Post Title - ${Date.now()}`,
    content: "This is the content of the test post.",
  };
  const createdPost: IPost = await api.functional.posts.posts.post(connection, { body: postInput });
  typia.assert<IPost>(createdPost);

  // 4. Retrieve the post by its ID
  const retrieved: IPost = await api.functional.posts.posts.getById(connection, {
    id: createdPost.id,
  });
  typia.assert<IPost>(retrieved);

  // 5. Assertions for field match and presence
  TestValidator.equals("id match")(createdPost.id)(retrieved.id);
  TestValidator.equals("author (user_id) match")(createdPost.user_id)(retrieved.user_id);
  TestValidator.equals("category_id match")(createdPost.category_id)(retrieved.category_id);
  TestValidator.equals("title match")(createdPost.title)(retrieved.title);
  TestValidator.equals("content match")(createdPost.content)(retrieved.content);
  TestValidator.equals("created_at match")(createdPost.created_at)(retrieved.created_at);
  TestValidator.equals("updated_at match")(createdPost.updated_at)(retrieved.updated_at);
  TestValidator.equals("deleted_at match")(
    createdPost.deleted_at ?? null
  )(retrieved.deleted_at ?? null);

  // 6. Field presence and type assertions
  typia.assert<string & tags.Format<"uuid">>(retrieved.id);
  typia.assert<string & tags.Format<"uuid">>(retrieved.user_id);
  typia.assert<string & tags.Format<"uuid">>(retrieved.category_id);
  typia.assert<string>(retrieved.title);
  typia.assert<string>(retrieved.content);
  typia.assert<string & tags.Format<"date-time">>(retrieved.created_at);
  typia.assert<string & tags.Format<"date-time">>(retrieved.updated_at);
  if (retrieved.deleted_at !== undefined && retrieved.deleted_at !== null) {
    typia.assert<string & tags.Format<"date-time">>(retrieved.deleted_at);
  }
}

