import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { ICategory } from "@ORGANIZATION/PROJECT-api/lib/structures/ICategory";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import typia from "typia";

export async function test_update_post_with_valid_data(connection: api.IConnection): Promise<void> {
  // 1. Create user
  const userCreate: IUser.ICreate = {
    user_role_id: typia.random<string>(),
    username: `user_${Math.random().toString(36).substring(2, 8)}`,
    email: `user_${Math.random().toString(36).substring(2, 8)}@test.com`,
    password: "TestPass123!",
    status: "active",
  };
  const user: IUser = await api.functional.core.users.post(connection, { body: userCreate });
  typia.assert<IUser>(user);

  // 2. Create category
  const categoryCreate: ICategory.ICreate = {
    name: `category_${Math.random().toString(36).substring(2, 8)}`,
    description: "A test category for posts update scenario." 
  };
  const category: ICategory = await api.functional.core.categories.post(connection, { body: categoryCreate });
  typia.assert<ICategory>(category);

  // 3. Create post
  const postCreate: IPost.ICreate = {
    category_id: category.id,
    title: "Original Title",
    content: "Original post content for updating.",
    user_id: user.id,
  };
  const post: IPost = await api.functional.posts.posts.post(connection, { body: postCreate });
  typia.assert<IPost>(post);

  // 4. Prepare post update (change title, content, category)
  const updateTitle = "Updated Title";
  const updateContent = "Updated content body!";
  // Optional: Change category (create another category)
  const newCategoryCreate: ICategory.ICreate = {
    name: `category_${Math.random().toString(36).substring(2, 8)}`,
    description: "Another category to test category_id change."
  };
  const newCategory: ICategory = await api.functional.core.categories.post(connection, { body: newCategoryCreate });
  typia.assert<ICategory>(newCategory);

  const postUpdate: IPost.IUpdate = {
    title: updateTitle,
    content: updateContent,
    category_id: newCategory.id,
  };

  // 5. Update the post
  const updated: IPost = await api.functional.posts.posts.putById(connection, {
    id: post.id,
    body: postUpdate,
  });
  typia.assert<IPost>(updated);

  // 6. Validate the updates
  TestValidator.equals("updated.title")<string>(updateTitle)(updated.title);
  TestValidator.equals("updated.content")<string>(updateContent)(updated.content);
  TestValidator.equals("updated.category_id")<string>(newCategory.id)(updated.category_id);
  TestValidator.equals("updated.id should be unchanged")<string>(post.id)(updated.id);
  TestValidator.equals("updated.user_id should be unchanged")<string>(post.user_id)(updated.user_id);

  // 7. Optionally, check updated_at is changed
  TestValidator.predicate("updated_at changed")(() => updated.updated_at !== post.updated_at);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import { ICategory } from "@ORGANIZATION/PROJECT-api/lib/structures/ICategory";

export async function test_update_post_with_invalid_fields(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create user (author)
  const user: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: typia.random<string & tags.Format<"uuid">>(),
      username: `author_${Math.random().toString(36).slice(2)}`,
      email: `author_${Math.random().toString(36).slice(2)}@test.com`,
      password: "password!123",
      bio: "test user",
      status: "active",
    },
  });
  typia.assert<IUser>(user);

  // Step 2: Create a category
  const category: ICategory = await api.functional.core.categories.post(connection, {
    body: {
      name: `category_${Math.random().toString(36).slice(2)}`,
      description: "Discussion category",
    },
  });
  typia.assert<ICategory>(category);

  // Step 3: Create a valid post for updating
  const post: IPost = await api.functional.posts.posts.post(connection, {
    body: {
      category_id: category.id,
      title: "Valid Post Title",
      content: "A valid post body.",
      user_id: user.id,
    },
  });
  typia.assert<IPost>(post);

  // Step 4: Try invalid update scenarios
  // (a) Empty content
  await TestValidator.httpError("empty content should fail")(400)(async () => {
    await api.functional.posts.posts.putById(connection, {
      id: post.id,
      body: { content: "" },
    });
  });

  // (b) Empty title
  await TestValidator.httpError("empty title should fail")(400)(async () => {
    await api.functional.posts.posts.putById(connection, {
      id: post.id,
      body: { title: "" },
    });
  });

  // (c) Invalid category_id (not a uuid)
  await TestValidator.httpError("invalid category_id should fail")(400)(async () => {
    await api.functional.posts.posts.putById(connection, {
      id: post.id,
      body: { category_id: "not-a-uuid" as any },
    });
  });

  // (d) Both title and content empty or undefined
  await TestValidator.httpError("missing fields should fail")(400)(async () => {
    await api.functional.posts.posts.putById(connection, {
      id: post.id,
      body: {}, // no updatable fields
    });
  });

  // (e) Extra, non-existent field in body
  await TestValidator.httpError("extra field should fail")(400)(async () => {
    await api.functional.posts.posts.putById(connection, {
      id: post.id,
      // @ts-expect-error: purposely inject invalid extra field
      body: { title: "Test", not_real_field: 123 },
    });
  });

  // (f) Valid update (title and content both updated)
  const updated: IPost = await api.functional.posts.posts.putById(connection, {
    id: post.id,
    body: { title: "Updated!", content: "Updated content." },
  });
  typia.assert<IPost>(updated);
  TestValidator.equals("post updated")<IPost>({
    ...post,
    title: "Updated!",
    content: "Updated content.",
  })({ ...updated, updated_at: post.updated_at }); // Ignore updated_at in strict check
}

