import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import { IComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IComment";
import { IPageIComment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIComment";
import typia from "typia";

export async function test_list_comments_with_pagination_and_filters(connection: api.IConnection): Promise<void> {
  // Step 1: Create users
  const user1: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: "11111111-1111-1111-1111-111111111111", // Should refer to existing user_role
      username: "commenter1" + Date.now(),
      email: `u1_${Date.now()}@test.com`,
      password: "password1!",
      bio: "User 1 for comments",
      status: "active",
    }
  });
  const user2: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: "11111111-1111-1111-1111-111111111111",
      username: "commenter2" + Date.now(),
      email: `u2_${Date.now()}@test.com`,
      password: "password2!",
      bio: "User 2 for comments",
      status: "active",
    }
  });
  // Step 2: Create posts
  const post1: IPost = await api.functional.posts.posts.post(connection, {
    body: {
      category_id: "22222222-2222-2222-2222-222222222222", // Should refer to existing category
      title: `Post 1 at ${Date.now()}`,
      content: "Post 1 for comment list test.",
      user_id: user1.id,
    },
  });
  const post2: IPost = await api.functional.posts.posts.post(connection, {
    body: {
      category_id: "22222222-2222-2222-2222-222222222222",
      title: `Post 2 at ${Date.now()}`,
      content: "Post 2 for comment list test.",
      user_id: user2.id,
    },
  });
  // Step 3: Add comments; 4-user/post combos, 2 comments each
  const comments: IComment[] = [];
  for (const post of [post1, post2]) {
    for (const user of [user1, user2]) {
      for (let i = 0; i < 2; ++i) {
        const comment: IComment = await api.functional.posts.comments.post(connection, {
          body: {
            user_id: user.id,
            post_id: post.id,
            content: `Comment ${i + 1} by ${user.username} on post ${post.title}`,
          },
        });
        comments.push(comment);
      }
    }
  }
  // Step 4: Pagination - limit/offset
  {
    const resp: IPageIComment = await api.functional.posts.comments.patch(connection, {
      body: { limit: 3, offset: 0 },
    });
    typia.assert(resp);
    TestValidator.predicate("Page should have at most 3 items")(resp.data.length <= 3);
    TestValidator.predicate("Pagination meta present")(!!resp.pagination);
  }
  {
    const resp: IPageIComment = await api.functional.posts.comments.patch(connection, {
      body: { limit: 3, offset: 3 },
    });
    typia.assert(resp);
    TestValidator.predicate("Page 2 items present (may be fewer than 3)")(resp.data.length >= 0 && resp.data.length <= 3);
  }
  // Step 5: Filter by post_id
  {
    const resp: IPageIComment = await api.functional.posts.comments.patch(connection, {
      body: { postId: post1.id },
    });
    typia.assert(resp);
    for (const summary of resp.data) {
      TestValidator.equals("Filter by post")(post1.id)(summary.post_id);
    }
  }
  // Filter by user_id
  {
    const resp: IPageIComment = await api.functional.posts.comments.patch(connection, {
      body: { userId: user2.id },
    });
    typia.assert(resp);
    for (const summary of resp.data) {
      TestValidator.equals("Filter by user")(user2.id)(summary.user_id);
    }
  }
  // Filter by both
  {
    const resp: IPageIComment = await api.functional.posts.comments.patch(connection, {
      body: { postId: post2.id, userId: user1.id },
    });
    typia.assert(resp);
    for (const summary of resp.data) {
      TestValidator.equals("Post")(post2.id)(summary.post_id);
      TestValidator.equals("User")(user1.id)(summary.user_id);
    }
  }
  // Step 6: Sorting (created_at desc/asc)
  {
    const respAsc: IPageIComment = await api.functional.posts.comments.patch(connection, {
      body: { sort: "created_at:asc" }
    });
    typia.assert(respAsc);
    for (let i = 1; i < respAsc.data.length; ++i) {
      TestValidator.predicate("Asc sorted")(
        respAsc.data[i-1].created_at <= respAsc.data[i].created_at
      );
    }
    const respDesc: IPageIComment = await api.functional.posts.comments.patch(connection, {
      body: { sort: "created_at:desc" }
    });
    typia.assert(respDesc);
    for (let i = 1; i < respDesc.data.length; ++i) {
      TestValidator.predicate("Desc sorted")(
        respDesc.data[i-1].created_at >= respDesc.data[i].created_at
      );
    }
  }
  // Step 7: Edge case - out-of-range
  {
    const resp: IPageIComment = await api.functional.posts.comments.patch(connection, {
      body: { limit: 2, offset: 9999 },
    });
    typia.assert(resp);
    TestValidator.equals("Out-of-range page is empty")([] as IComment.ISummary[])(resp.data);
  }
  // Step 8: Filter for post/user with no comments
  {
    const resp: IPageIComment = await api.functional.posts.comments.patch(connection, {
      body: { postId: "e0000000-0000-0000-0000-000000000000" },
    });
    typia.assert(resp);
    TestValidator.equals("No comments on fake post")([] as IComment.ISummary[])(resp.data);
  }
}

