export async function test_get_comment_by_valid_id(connection: api.IConnection): Promise<void> {
  // 1. Create a user
  const user: IUser = await api.functional.core.users.post(connection, {
    body: {
      user_role_id: typia.random<typeof user["user_role_id"]>(),
      username: `testuser_${Math.random().toString(36).substring(2, 10)}`,
      email: `test_${Math.random().toString(36).substring(2, 10)}@example.com`,
      password: "SecureP@ssw0rd!",
      status: "active",
      bio: "E2E test user",
    },
  });
  typia.assert<IUser>(user);

  // 2. Create a post by this user (category id must be valid format)
  const post: IPost = await api.functional.posts.posts.post(connection, {
    body: {
      category_id: typia.random<typeof post["category_id"]>(),
      title: "Test Post for Comment Detail E2E",
      content: "Post content for comment fetching test.",
      user_id: user.id,
    },
  });
  typia.assert<IPost>(post);

  // 3. Create a comment on the above post by this user
  const comment: IComment = await api.functional.posts.comments.post(connection, {
    body: {
      user_id: user.id,
      post_id: post.id,
      content: "This is a test comment for detail fetch.",
    },
  });
