  };
  const user: IUser = await api.functional.core.users.post(connection, { body: userInput });
  typia.assert(user);

  // 2. Create post
  const postInput: IPost.ICreate = {
    category_id: typia.random<string>(), // Replace with a valid category UUID if needed
    title: "A post for comment test",
    content: "Original post content.",
    user_id: user.id,
  };
