    // 3. User1 and User2 vote on the post
    const vote1: IVote = await api.functional.votes.votes.post(connection, {
        body: {
            user_id: user1.id,
            value: 1,
            post_id: post.id,
        },
    });
    const vote2: IVote = await api.functional.votes.votes.post(connection, {
        body: {
            user_id: user2.id,
            value: -1,
            post_id: post.id,
        },
    });
    typia.assert(vote1);
    typia.assert(vote2);

    // 4. Retrieve all votes (no filters)
    const allVotesPage: IPageIVote = await api.functional.votes.votes.patch(connection, {
        body: {},
    });
    typia.assert(allVotesPage);
    TestValidator.predicate("Should have at least 2 votes...")(() => allVotesPage.data.length >= 2);

    // 5. Filter by user1
    const user1Votes: IPageIVote = await api.functional.votes.votes.patch(connection, {
        body: { userId: user1.id },
    });
    typia.assert(user1Votes);
    TestValidator.predicate("User1 vote filter")(user1Votes.data.every(v => v.user_id === user1.id));
    TestValidator.predicate("User1 vote count >=1")(user1Votes.data.length >= 1);

    // 6. Filter by post
    const postVotes: IPageIVote = await api.functional.votes.votes.patch(connection, {
        body: { postId: post.id },
    });
    typia.assert(postVotes);
    TestValidator.predicate("Post vote filter")(postVotes.data.every(v => v.post_id === post.id));
    TestValidator.predicate("Post vote user set")(new Set(postVotes.data.map(v => v.user_id)).has(user1.id) && new Set(postVotes.data.map(v => v.user_id)).has(user2.id));

    // 7. Pagination (limit=1)
    const pagedVotes: IPageIVote = await api.functional.votes.votes.patch(connection, {
        body: { limit: 1 },
    });
    typia.assert(pagedVotes);
    TestValidator.equals("Limit 1 pagination")(
      1
    )(pagedVotes.data.length);
    TestValidator.predicate("Paginated metadata shows more or equal pages")(() => pagedVotes.pagination.pages >= 1);

    // 8. Negative test: invalid filter with both postId + commentId
    await TestValidator.error("Mutually exclusive filter (postId + commentId)")(
        async () => {
            await api.functional.votes.votes.patch(connection, {
                body: {
                    postId: post.id,
                    commentId: typia.random<string>(),
                },
            });
        }
    );

    // 9. Validate all responses structurally
    [allVotesPage, user1Votes, postVotes, pagedVotes].forEach(page => {
        typia.assert<IPageIVote>(page);
        page.data.forEach((vote) => typia.assert<IVote.ISummary>(vote));
    });
}

