  const createUserInput: IUser.ICreate = {
    user_role_id: typia.random<string>(),
    username: `vote-test-user-${Math.random().toString(36).substring(2, 8)}`,
    email: `vote${Math.random().toString(36).substring(2, 8)}@example.com`,
