export async function test_create_vote_with_valid_data(connection: api.IConnection): Promise<void> {
  // Step 1: Create a new user
  const userInput: IUser.ICreate = {
    user_role_id: typia.random<string>(),
    username: `user_` + Math.random().toString(36).substring(2, 10),
    email: `test_` + Math.random().toString(36).substring(2, 8) + `@mail.com`,
    bio: "Test user for vote e2e (happy path)",
    password: "TestPassword!123",
    status: "active",
  };
  const user: IUser = await api.functional.core.users.post(connection, { body: userInput });
  typia.assert<IUser>(user);
  TestValidator.predicate("user.id uuid")(!!user.id && typeof user.id === "string");

  // Step 2: Create a new post (by that user)
  const postInput: IPost.ICreate = {
    category_id: typia.random<string>(),
    title: "Vote Test Post: " + Math.random().toString(36).substring(2, 10),
    content: "This is a post to test voting endpoints.",
    user_id: user.id,
  };
  const post: IPost = await api.functional.posts.posts.post(connection, { body: postInput });
  typia.assert<IPost>(post);
  TestValidator.equals("post.user_id")(post.user_id)(user.id);
  TestValidator.predicate("post.id uuid")(!!post.id && typeof post.id === "string");

  // Step 3a: Create an upvote for the post
  const voteInputUp: IVote.ICreate = {
    user_id: user.id,
    value: 1,
    post_id: post.id,
    comment_id: null,
  };
  const upvote: IVote = await api.functional.votes.votes.post(connection, { body: voteInputUp });
  typia.assert<IVote>(upvote);
  TestValidator.equals("vote.user_id")(upvote.user_id)(user.id);
  TestValidator.equals("vote.post_id")(upvote.post_id)(post.id);
  TestValidator.predicate("upvote.value valid")(upvote.value === 1);
  TestValidator.predicate("upvote has no comment_id")(!!!upvote.comment_id);

  // Step 3b: Also check with downvote by a distinct user on another new post
  const userInput2: IUser.ICreate = {
    ...userInput,
    username: `user2_` + Math.random().toString(36).substring(2, 10),
    email: `test2_` + Math.random().toString(36).substring(2, 8) + `@mail.com`,
  };
  const user2: IUser = await api.functional.core.users.post(connection, { body: userInput2 });

  const postInput2: IPost.ICreate = {
    ...postInput,
    title: "Vote Test Post2: " + Math.random().toString(36).substring(2, 10),
    user_id: user2.id,
  };
  const post2: IPost = await api.functional.posts.posts.post(connection, { body: postInput2 });

  const voteInputDown: IVote.ICreate = {
    user_id: user2.id,
    value: -1,
    post_id: post2.id,
    comment_id: null,
  };
  const downvote: IVote = await api.functional.votes.votes.post(connection, { body: voteInputDown });
  typia.assert<IVote>(downvote);
  TestValidator.equals("vote.user_id")(downvote.user_id)(user2.id);
  TestValidator.equals("vote.post_id")(downvote.post_id)(post2.id);
  TestValidator.predicate("downvote.value valid")(downvote.value === -1);
  TestValidator.predicate("downvote has no comment_id")(!!!downvote.comment_id);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
