import typia from "typia";

export async function test_create_vote_with_duplicate_user_target(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a test user
  const userInput: IUser.ICreate = {
    user_role_id: typia.random<string>(), // Assume test system role UUIDs are ok here
    username: `testuser_${Date.now()}`,
    email: `test_${Date.now()}@test.com`,
    password: "Test1234!",
    status: "active",
  };
  const user: IUser = await api.functional.core.users.post(connection, {
    body: userInput,
  });
  typia.assert<IUser>(user);

  // 2. Create a test post authored by the user (category_id required)
  const postInput: IPost.ICreate = {
    category_id: typia.random<string>(), // Use random category for test
    title: `Test post ${Date.now()}`,
    content: "This is a post for vote uniqueness testing.",
    user_id: user.id,
  };
  const post: IPost = await api.functional.posts.posts.post(connection, {
    body: postInput,
  });
  typia.assert<IPost>(post);

  // 3. Cast an initial vote by the user on the post (+1 upvote)
  const voteInput: IVote.ICreate = {
    user_id: user.id,
    value: 1,
    post_id: post.id,
  };
  const firstVote = await api.functional.votes.votes.post(connection, {
    body: voteInput,
  });
