import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IUser } from "@ORGANIZATION/PROJECT-api/lib/structures/IUser";
import { IPost } from "@ORGANIZATION/PROJECT-api/lib/structures/IPost";
import { IVote } from "@ORGANIZATION/PROJECT-api/lib/structures/IVote";
import typia from "typia";

export async function test_update_vote_with_valid_data(connection: api.IConnection): Promise<void> {
    // 1. Register voter user
    const userInput: IUser.ICreate = {
        user_role_id: typia.random<string>(), // must be valid, for test assume accepted
        username: `voteuser_${Date.now()}`,
        email: `voter_${Date.now()}@test.com`,
        password: "pw1234test!",
        status: "active"
    };
    const user = await api.functional.core.users.post(connection, { body: userInput });
    typia.assert<IUser>(user);

    // 2. Create a post
    const postInput: IPost.ICreate = {
        category_id: typia.random<string>(), // must be valid, for test assume accepted
        title: "Vote Target Post",
        content: "Initial post for voting",
        user_id: user.id,
    };
    const post = await api.functional.posts.posts.post(connection, { body: postInput });
    typia.assert<IPost>(post);

    // 3. Create a vote
    const voteInput: IVote.ICreate = {
        user_id: user.id,
        value: 1,
        post_id: post.id,
    };
    const vote = await api.functional.votes.votes.post(connection, { body: voteInput });
    typia.assert<IVote>(vote);

    // 4. Update the vote value (flip between +1/-1)
    const updatedValue = vote.value === 1 ? -1 : 1;
    const voteUpdate: IVote.IUpdate = {
        value: updatedValue
    };
    const updatedVote = await api.functional.votes.votes.putById(connection, { id: vote.id, body: voteUpdate });
    typia.assert<IVote>(updatedVote);

    // 5. Verify new value is stored, and target fields remain unchanged
    TestValidator.equals("updated vote id")(vote.id)(updatedVote.id);
    TestValidator.equals("updated user_id")(vote.user_id)(updatedVote.user_id);
    TestValidator.equals("updated post_id")(vote.post_id)(updatedVote.post_id);
    TestValidator.equals("updated value")(updatedValue)(updatedVote.value);
}

