import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";

export async function test_update_api_client_metadata_and_handle_errors(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a client to update
  const createInput: IApiClients.ICreate = {
    client_name: `TestClient_${Date.now()}`,
    contact_email: `e2e_update_${Date.now()}@test.com`,
    public_key: "-----BEGIN PUBLIC KEY-----Test-----END PUBLIC KEY-----",
    description: "Original description"
  };

  const created = await api.functional.apiOmnichannel.apiClients.post(connection, { body: createInput });
  typia.assert<IApiClients>(created);

  // 2. Happy path update: All mutable fields
  const updateInput: IApiClients.IUpdate = {
    description: "Updated via E2E test",
    contact_email: `e2e_updated_${Date.now()}@test.com`,
    public_key: "-----BEGIN PUBLIC KEY-----Changed-----END PUBLIC KEY-----",
    is_active: false,
  };

  const updated = await api.functional.apiOmnichannel.apiClients.putById(connection, {
    id: created.id,
    body: updateInput,
  });
  typia.assert<IApiClients>(updated);
  TestValidator.equals("description updated")<string | undefined>(updateInput.description)(updated.description);
  TestValidator.equals("contact_email updated")<string | undefined>(updateInput.contact_email)(updated.contact_email);
  TestValidator.equals("public_key updated")<string | undefined>(updateInput.public_key)(updated.public_key);
  TestValidator.equals("is_active updated")<boolean | undefined>(updateInput.is_active)(updated.is_active);

  //3. Invalid/malformed UUID
  await TestValidator.httpError("malformed UUID should yield error")(404,400)(async () => {
    await api.functional.apiOmnichannel.apiClients.putById(connection, {
      id: "not-a-uuid" as string & tags.Format<"uuid">,
      body: updateInput,
    });
  });

  //4. Valid but non-existent UUID
  const randomUuid = typia.random<string & tags.Format<"uuid">>();
  // Avoid collision with real ID
  if (randomUuid !== created.id) {
    await TestValidator.httpError("non-existent client ID should 404")(404)(async () => {
      await api.functional.apiOmnichannel.apiClients.putById(connection, {
        id: randomUuid,
        body: updateInput,
      });
    });
  }

  //5. Empty/missing body (invalid request)
  await TestValidator.httpError("missing update body should 400")(400)(async () => {
    // @ts-expect-error: Deliberately omit body
    await api.functional.apiOmnichannel.apiClients.putById(connection, {
      id: created.id,
      body: {},
    });
  });

  //6. Attempt to mutate unmodifiable fields (should be ignored or error)
  // They should be ignored per schema, we'll try adding bogus keys
  await TestValidator.httpError("unmodifiable fields in update should error or be ignored")(400,422)(async () => {
    // @ts-expect-error
    await api.functional.apiOmnichannel.apiClients.putById(connection, {
      id: created.id,
      body: {
        description: "Try changing client_name & id",
        // @ts-expect-error
        client_name: "ShouldNotChange",
        // @ts-expect-error
        id: "00000000-0000-0000-0000-000000000000"
      },
    });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";

export async function test_soft_delete_api_client_and_handle_invalid_id(connection: api.IConnection): Promise<void> {
  // 1. Create a new API client
  const createInput: IApiClients.ICreate = {
    client_name: `TestClient_${Date.now()}`,
    contact_email: `test${Date.now()}@example.com`,
    public_key: "-----BEGIN PUBLIC KEY-----\nMIIBIjANBgkqhkiG9w0BAQEFAAOCAQ8AMIIBCgKCAQEArandomPubKeystring==\n-----END PUBLIC KEY-----",
    description: "For E2E test soft delete and error validation."
  };
  const created = await api.functional.apiOmnichannel.apiClients.post(connection, { body: createInput });
  typia.assert<IApiClients>(created);

  // 2. Soft-delete the created client
  const erased = await api.functional.apiOmnichannel.apiClients.eraseById(connection, { id: created.id });
  typia.assert<IApiClients>(erased);
  TestValidator.equals("is_active should be false")<boolean>(false)(erased.is_active);
  TestValidator.predicate("deleted_at should be set")(() => typeof erased.deleted_at === "string" && erased.deleted_at.length > 0);

  // 3. Try deleting the same client again (should error or remain idempotent)
  await TestValidator.httpError("deleting already deleted client should return 404 or business error")(404, 400)(() =>
    api.functional.apiOmnichannel.apiClients.eraseById(connection, { id: created.id })
  );

  // 4. Try deleting a client with an invalid (malformed) UUID
  const malformedId = "not-a-valid-uuid";
  await TestValidator.httpError("malformed UUID should fail validation")(400)(() =>
    api.functional.apiOmnichannel.apiClients.eraseById(connection, { id: malformedId as string & tags.Format<"uuid"> })
  );

  // 5. Try deleting a client with a well-formed, but non-existent UUID
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("non-existent client should return 404")(404)(() =>
    api.functional.apiOmnichannel.apiClients.eraseById(connection, { id: nonExistentId })
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import { IApiTokens } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiTokens";
import { IPageIApiTokens } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIApiTokens";

export async function test_list_and_search_api_tokens_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create API client
  const clientInput: IApiClients.ICreate = {
    client_name: `Test Client ${Date.now()}`,
    contact_email: `client${Date.now()}@example.com`,
    public_key: "dummy_pubkey",
    description: "E2E test client for token search."
  };
  const client: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, { body: clientInput });
  typia.assert(client);

  // 2. Create several API tokens for the client (different scopes and expiries)
  const now = new Date();
  const tokens: IApiToken[] = [];
  for (let i = 0; i < 3; ++i) {
    const issued_at = new Date(now.getTime() - i * 1000 * 60 * 60).toISOString();
    const expires_at = new Date(now.getTime() + (i + 1) * 1000 * 60 * 60).toISOString();
    const tokenInput: IApiToken.ICreate = {
      api_client_id: client.id,
      scope: `read:scope${i}`,
      issued_at,
      expires_at
    };
    const token: IApiToken = await api.functional.apiOmnichannel.apiTokens.post(connection, { body: tokenInput });
    typia.assert(token);
    tokens.push(token);
  }

  // 3. List tokens with default pagination/filter
  const respDefault: IPageIApiTokens = await api.functional.apiOmnichannel.apiTokens.patch(connection, {
    body: {}
  });
  typia.assert(respDefault);
  TestValidator.predicate("data is array")(() => Array.isArray(respDefault.data));
  TestValidator.predicate("pagination exists")(() => !!respDefault.pagination);
  TestValidator.predicate("records >= 3")(() => respDefault.pagination.records >= 3);

  // 4. Filter by api_client_id
  const respClient: IPageIApiTokens = await api.functional.apiOmnichannel.apiTokens.patch(connection, {
    body: { api_client_id: client.id }
  });
  typia.assert(respClient);
  TestValidator.predicate("all tokens belong to client")(() =>
    respClient.data.every(t => t.api_client_id === client.id)
  );

  // 5. Filter by active tokens (assume all just created tokens are active)
  const respActive: IPageIApiTokens = await api.functional.apiOmnichannel.apiTokens.patch(connection, {
    body: { active: true }
  });
  typia.assert(respActive);
  TestValidator.predicate("active tokens returned")(() =>
    respActive.data.every(t => t.active === true || t.revoked_at === undefined)
  );

  // 6. Filter by issued_from/issued_to (use tokens[0] as reference)
  const firstIssue = tokens[0].issued_at;
  const lastIssue = tokens[tokens.length - 1].issued_at;
  const respIssued: IPageIApiTokens = await api.functional.apiOmnichannel.apiTokens.patch(connection, {
    body: { issued_from: lastIssue, issued_to: firstIssue }
  });
  typia.assert(respIssued);
  TestValidator.predicate("tokens in issued window")(() =>
    respIssued.data.every(t => t.issued_at >= lastIssue && t.issued_at <= firstIssue)
  );

  // 7. Extreme pagination: high page number should yield no data
  const extremeResp: IPageIApiTokens = await api.functional.apiOmnichannel.apiTokens.patch(connection, {
    body: { page: 1000, limit: 1 }
  });
  typia.assert(extremeResp);
  TestValidator.equals("extreme pagination has no data")<IApiTokens[]>([])(extremeResp.data);

  // 8. Invalid filter: wrong api_client_id
  const respBadClient: IPageIApiTokens = await api.functional.apiOmnichannel.apiTokens.patch(connection, {
    body: { api_client_id: "00000000-0000-0000-0000-000000000000" }
  });
  typia.assert(respBadClient);
  TestValidator.equals("no tokens for bad client")<IApiTokens[]>([])(respBadClient.data);

  // 9. Negative test: invalid parameter (string instead of int for page)
  await TestValidator.httpError("invalid parameter should fail")(400)(async () => {
    await api.functional.apiOmnichannel.apiTokens.patch(connection, {
      body: { page: "not-an-int" as any }
    });
  });

  // 10. Validate pagination meta
  const pageMetaResp: IPageIApiTokens = await api.functional.apiOmnichannel.apiTokens.patch(connection, {
    body: { limit: 2, page: 1 }
  });
  typia.assert(pageMetaResp);
  TestValidator.predicate("limit meta matches")(() => pageMetaResp.pagination.limit === 2);
  TestValidator.predicate("page meta matches")(() => pageMetaResp.pagination.current === 1);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import { IApiTokens } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiTokens";
import typia, { tags } from "typia";

export async function test_get_api_token_by_id_with_valid_and_invalid_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new API client
  const clientInput: IApiClients.ICreate = {
    client_name: `test-client-${Math.random().toString(36).substring(2, 12)}`,
    contact_email: `client${Math.random().toString(36).substring(2, 12)}@example.com`,
    public_key: "test-public-key",
    description: "Test client for token retrieval scenario",
  };
  const client: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, {
    body: clientInput,
  });
  typia.assert<IApiClients>(client);

  // 2. Create a new API token for the client
  // Use now/expiry with fixed offsets for determinism
  const now = new Date();
  const issuedAt = new Date(now.getTime()).toISOString();
  const expiresAt = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000).toISOString(); // 1 week expiry
  const tokenInput: IApiToken.ICreate = {
    api_client_id: client.id,
    scope: "read:write:test",
    issued_at: issuedAt as string & tags.Format<"date-time">,
    expires_at: expiresAt as string & tags.Format<"date-time">,
  };
  const token: IApiToken = await api.functional.apiOmnichannel.apiTokens.post(connection, {
    body: tokenInput,
  });
  typia.assert<IApiToken>(token);

  // 3. Retrieve the created token by its ID
  const tokenMeta: IApiTokens = await api.functional.apiOmnichannel.apiTokens.getById(connection, {
    id: token.id,
  });
  typia.assert<IApiTokens>(tokenMeta);
  // Field validation: core fields should match
  TestValidator.equals("token id matches")(token.id)(tokenMeta.id);
  TestValidator.equals("api_client_id matches")(token.api_client_id)(tokenMeta.api_client_id);
  TestValidator.equals("scope matches")(token.scope)(tokenMeta.scope);
  TestValidator.equals("issued_at matches")(token.issued_at)(tokenMeta.issued_at);
  TestValidator.equals("expires_at matches")(token.expires_at)(tokenMeta.expires_at);
  // revoked_at may be undefined/null, don't compare if not present

  // 4. Attempt to retrieve a token with a non-existent UUID
  const randomFakeId: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("should fail for non-existent token")(404)(async () => {
    await api.functional.apiOmnichannel.apiTokens.getById(connection, {
      id: randomFakeId,
    });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";

export async function test_create_api_token_for_client_and_check_constraints(
  connection: api.IConnection
): Promise<void> {
  // 1. Create a valid API client
  const clientInput: IApiClients.ICreate = {
    client_name: `client_${Math.random().toString(36).substring(2, 8)}`,
    contact_email: `test_${Math.random().toString(36).substring(2, 8)}@example.com`,
    public_key: "-----BEGIN PUBLIC KEY-----\nFAKEKEYDATA\n-----END PUBLIC KEY-----",
    description: "E2E Integration test client"
  };
  const client: IApiClients = await api.functional.apiOmnichannel.apiClients.post(
    connection,
    {
      body: clientInput
    }
  );
  typia.assert(client);

  // 2. Prepare valid API token request
  const now = new Date();
  const issuedAt = now.toISOString();
  const expiresAt = new Date(now.getTime() + 3600 * 1000).toISOString();
  const tokenInput: IApiToken.ICreate = {
    api_client_id: client.id,
    scope: "read:all write:limited",
    issued_at: issuedAt,
    expires_at: expiresAt
  };
  const token: IApiToken = await api.functional.apiOmnichannel.apiTokens.post(
    connection,
    { body: tokenInput }
  );
  typia.assert(token);
  TestValidator.equals("api_client_id")(tokenInput.api_client_id)(token.api_client_id);
  TestValidator.equals("scope")(tokenInput.scope)(token.scope);

  // 3. Attempt duplicate token creation (same client, scope, issued/expires)
  const duplicateToken = await api.functional.apiOmnichannel.apiTokens.post(
    connection,
    { body: tokenInput }
  );
  typia.assert(duplicateToken);
  TestValidator.equals("api_client_id (duplicate)")(tokenInput.api_client_id)(duplicateToken.api_client_id);

  // 4. Try creating token with non-existent client ID
  const nonExistentInput: IApiToken.ICreate = {
    ...tokenInput,
    api_client_id: "00000000-0000-4000-8000-000000000000" // presumably fake UUID
  };
  await TestValidator.httpError("token for non-existent client should fail")(400, 404)(async () => {
    await api.functional.apiOmnichannel.apiTokens.post(
      connection,
      { body: nonExistentInput }
    );
  });

  // 5. Attempt token creation missing 'scope'
  const missingScopeInput = { ...tokenInput };
  // @ts-expect-error Testing missing required field
  delete missingScopeInput.scope;
  await TestValidator.error("missing required 'scope' causes validation error")(() =>
    // @ts-ignore
    api.functional.apiOmnichannel.apiTokens.post(connection, { body: missingScopeInput })
  );

  // 6. Attempt token creation missing 'expires_at'
  const missingExpiresInput = { ...tokenInput };
  // @ts-expect-error Testing missing required field
  delete missingExpiresInput.expires_at;
  await TestValidator.error("missing required 'expires_at' causes validation error")(() =>
    // @ts-ignore
    api.functional.apiOmnichannel.apiTokens.post(connection, { body: missingExpiresInput })
  );

  // 7. Invalid date-time string in issued_at
  const invalidDateInput: IApiToken.ICreate = {
    ...tokenInput,
    issued_at: "not-a-date",
    expires_at: "not-a-date"
  };
  await TestValidator.error("invalid date format should fail")(() =>
    // @ts-ignore
    api.functional.apiOmnichannel.apiTokens.post(connection, { body: invalidDateInput })
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";

export async function test_update_api_token_metadata_with_valid_and_invalid_id(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create API client
  const client: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, {
    body: {
      client_name: `test-client-${Date.now()}`,
      contact_email: `test${Date.now()}@test.com`,
      public_key: "test-public-key",
      description: "Integration test client",
    },
  });
  typia.assert(client);

  // 2. Create API token for the client
  const issuedAt = new Date().toISOString();
  const expiresAt = new Date(Date.now() + 7 * 24 * 3600 * 1000).toISOString();
  const token: IApiToken = await api.functional.apiOmnichannel.apiTokens.post(connection, {
    body: {
      api_client_id: client.id,
      scope: "read:all",
      issued_at: issuedAt,
      expires_at: expiresAt,
    },
  });
  typia.assert(token);

  // 3. Normal update using valid token ID
  const newExpiresAt = new Date(Date.now() + 14 * 24 * 3600 * 1000).toISOString();
  const updateBody: IApiToken.IUpdate = {
    scope: "read:limited",
    expires_at: newExpiresAt,
  };
  const updatedToken: IApiToken = await api.functional.apiOmnichannel.apiTokens.putById(connection, {
    id: token.id,
    body: updateBody,
  });
  typia.assert(updatedToken);
  TestValidator.equals("should update scope", undefined)(updateBody.scope!)(updatedToken.scope);
  TestValidator.equals("should update expires_at", undefined)(updateBody.expires_at!)(updatedToken.expires_at);

  // 4. Try updating non-existent token (random UUID)
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("update non-existent token should fail") (404)(async () => {
    await api.functional.apiOmnichannel.apiTokens.putById(connection, {
      id: nonExistentId,
      body: updateBody,
    });
  });

  // 5. Try updating with invalid ID format
  await TestValidator.httpError("invalid id format should fail") (400, 422)(async () => {
    await api.functional.apiOmnichannel.apiTokens.putById(connection, {
      id: "not-a-uuid" as any,
      body: updateBody,
    });
  });

  // 6. Attempt to update with invalid field values
  const badUpdateBody: any = {
    scope: "",
    expires_at: "not-a-date",
  };
  await TestValidator.httpError("invalid update body should fail") (400, 422)(async () => {
    await api.functional.apiOmnichannel.apiTokens.putById(connection, {
      id: token.id,
      body: badUpdateBody,
    });
  });

  // 7. Confirm the token has not been altered by previous invalid updates
  const confirmToken: IApiToken = await api.functional.apiOmnichannel.apiTokens.putById(connection, {
    id: token.id,
    body: {}, // Minimal update (no changes)
  });
  typia.assert(confirmToken);
  TestValidator.equals("scope still intact", undefined)(updatedToken.scope)(confirmToken.scope);
  TestValidator.equals("expires_at still intact", undefined)(updatedToken.expires_at)(confirmToken.expires_at);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import typia, { tags } from "typia";

export async function test_revoke_api_token_and_handle_invalid_ids(
  connection: api.IConnection,
): Promise<void> {
  // Step 1. Create API client
  const client: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, {
    body: {
      client_name: `test-client-${Date.now()}`,
      contact_email: `test-client-${Date.now()}@example.com`,
      public_key: undefined,
      description: "Test client for token revoke scenario"
    },
  });
  typia.assert(client);

  // Step 2. Create API token tied to the client
  const issuedAt = new Date().toISOString();
  const expiresAt = new Date(Date.now() + 1000 * 60 * 60 * 24).toISOString();
  const tokenInput: IApiToken.ICreate = {
    api_client_id: client.id,
    scope: "test-scope",
    issued_at: issuedAt,
    expires_at: expiresAt,
  };
  const token: IApiToken = await api.functional.apiOmnichannel.apiTokens.post(connection, {
    body: tokenInput,
  });
  typia.assert(token);
  TestValidator.equals("token api_client_id")(tokenInput.api_client_id)(token.api_client_id);
  TestValidator.predicate("token not revoked yet")(!token.revoked_at);

  // Step 3. Revoke (soft-delete) the token
  const revoked: IApiToken = await api.functional.apiOmnichannel.apiTokens.eraseById(connection, { id: token.id });
  typia.assert(revoked);
  TestValidator.equals("revoked id")(token.id)(revoked.id);
  TestValidator.predicate("revoked_at set after revocation")(!!revoked.revoked_at);

  // Step 4. Attempt to revoke again (should error)
  await TestValidator.httpError("revoke already revoked token")(404, 400, 409)(async () => {
    await api.functional.apiOmnichannel.apiTokens.eraseById(connection, { id: token.id });
  });

  // Step 5. Attempt to revoke a random invalid UUID
  const randomInvalidId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("revoke non-existent token")(404)(async () => {
    await api.functional.apiOmnichannel.apiTokens.eraseById(connection, { id: randomInvalidId });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IApiAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiAudit";
import { IPageIApiAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIApiAudit";

export async function test_list_search_api_audit_entries_with_varied_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create audit entries with various data
  const entries: IApiAudit[] = [];
  const base: IApiAudit.ICreate = typia.random<IApiAudit.ICreate>();

  // Create an array of entries for varied filters
  for (let i = 0; i < 4; ++i) {
    const entity: IApiAudit.ICreate = {
      ...base,
      api_client_id: typia.random<string & typia.tags.Format<"uuid">>(),
      api_token_id: typia.random<string & typia.tags.Format<"uuid">>(),
      actor_id: typia.random<string & typia.tags.Format<"uuid">>(),
      endpoint: `/endpoint/${i}`,
      http_method: i % 2 === 0 ? "GET" : "POST",
      is_success: i % 2 === 0,
      status_code: i % 2 === 0 ? 200 : 400,
      created_at: new Date(Date.now() - i * 100000).toISOString(),
    };
    const output = await api.functional.apiOmnichannel.apiAudits.post(
      connection,
      { body: entity },
    );
    typia.assert<IApiAudit>(output);
    entries.push(output);
  }

  // 2. Fetch all entries (no filters)
  const response_all = await api.functional.apiOmnichannel.apiAudits.patch(connection, {
    body: {},
  });
  typia.assert<IPageIApiAudit>(response_all);
  TestValidator.predicate("List API audits should include created entries")(
    () => entries.every((e) => response_all.data.some((r) => r.id === e.id)),
  );

  // 3. Filter by api_client_id
  const selected = entries[1];
  const filterByClient = await api.functional.apiOmnichannel.apiAudits.patch(connection, {
    body: { api_client_id: selected.api_client_id },
  });
  typia.assert<IPageIApiAudit>(filterByClient);
  TestValidator.predicate("Result should have at least 1 entry for specific client")(
    () => filterByClient.data.some((r) => r.api_client_id === selected.api_client_id),
  );

  // 4. Filter by actor_id
  const filterByActor = await api.functional.apiOmnichannel.apiAudits.patch(connection, {
    body: { actor_id: selected.actor_id },
