  typia.assert<IPageIApiAudit>(filterByActor);
  TestValidator.predicate("Result should have at least 1 entry for specific actor")(
    () => filterByActor.data.some((r) => r.actor_id === selected.actor_id),
  );

  // 5. Filter by endpoint and method
  const filterByOperation = await api.functional.apiOmnichannel.apiAudits.patch(connection, {
    body: {
      endpoint: selected.endpoint,
      http_method: selected.http_method,
    },
  });
  typia.assert<IPageIApiAudit>(filterByOperation);
  TestValidator.predicate("Endpoint/method filter must match one of the entries")(
    () => filterByOperation.data.some((r) => r.id === selected.id),
  );

  // 6. Filter by is_success (operation type)
  const filterBySuccess = await api.functional.apiOmnichannel.apiAudits.patch(connection, {
    body: { is_success: selected.is_success },
  });
  typia.assert<IPageIApiAudit>(filterBySuccess);
  TestValidator.predicate("Success filter must match one of the entries")(
    () => filterBySuccess.data.some((r) => r.is_success === selected.is_success),
  );

  // 7. Combine multiple filters and paginate
  const filterPaginated = await api.functional.apiOmnichannel.apiAudits.patch(connection, {
    body: {
      api_client_id: selected.api_client_id,
      actor_id: selected.actor_id,
      limit: 1,
      page: 1,
    },
  });
  typia.assert<IPageIApiAudit>(filterPaginated);
  TestValidator.predicate("Paginated response should contain up to limit records")(
    () => filterPaginated.data.length <= 1,
  );
  TestValidator.predicate("Pagination in meta is correct")(
    () => filterPaginated.pagination.limit === 1 && filterPaginated.pagination.current === 1,
  );

  // 8. Invalid UUID filters should produce safe/empty/error result
  const filterInvalidUUID = await api.functional.apiOmnichannel.apiAudits.patch(connection, {
    body: { api_client_id: "not-a-uuid" as any },
  });
  typia.assert<IPageIApiAudit>(filterInvalidUUID);
  TestValidator.predicate("Invalid uuid should result in no data/error")(
    () => filterInvalidUUID.data.length === 0,
  );

  // 9. Invalid type: wrong property type
  await TestValidator.error("Wrong status_code type triggers error")(
    async () => {
      // @ts-expect-error - purposely passing wrong type
      await api.functional.apiOmnichannel.apiAudits.patch(connection, {
        body: { status_code: "ERROR_CODE" },
      });
    },
  );
}

