import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";
import { IApiAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiAudit";

export async function test_update_api_audit_entry_by_id_and_edge_cases(connection: api.IConnection) {
  // 1. Create a new audit entry
  const created: IApiAudit = await api.functional.apiOmnichannel.apiAudits.post(connection, {
    body: typia.random<IApiAudit.ICreate>()
  });

  typia.assert<IApiAudit>(created);
  const auditId = created.id;

  // 2. Prepare a changed update object
  const update: IApiAudit.IUpdate = {
    api_client_id: created.api_client_id,
    api_token_id: created.api_token_id,
    integration_channel_id: created.integration_channel_id,
    endpoint: created.endpoint + "/changed",
    http_method: created.http_method,
    actor_id: created.actor_id,
    decision_log_id: created.decision_log_id,
    request_hash: "newhashxxxxxxxx",
    response_hash: "responsehashxxxx",
    is_success: !created.is_success,
    status_code: (created.status_code || 200) + 1,
    created_at: created.created_at,
  };

  // 3. Update audit entry
  const updated = await api.functional.apiOmnichannel.apiAudits.putById(connection, {
    id: auditId,
    body: update
  });
  typia.assert<IApiAudit>(updated);
  TestValidator.equals("Updated API audit endpoint")(update.endpoint)(updated.endpoint);
  TestValidator.equals("Updated API audit status_code")(update.status_code)(updated.status_code);
  TestValidator.equals("Updated API audit request_hash")(update.request_hash)(updated.request_hash);
  TestValidator.equals("Updated API audit is_success")(update.is_success)(updated.is_success);

  // 4. Check persistence: can be implicit by returned updated object
  // (No GET endpoint provided, so use returned data)
  TestValidator.equals("API audit entry ID persistence")(auditId)(updated.id);

  // 5. Non-existent ID
  await TestValidator.httpError("Update non-existent audit ID should 404")(404)(async () => {
    await api.functional.apiOmnichannel.apiAudits.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(),
      body: update
    });
  });

  // 6. Invalid ID format
  await TestValidator.httpError("Invalid audit ID format should 400 or 422")(400, 422)(async () => {
    await api.functional.apiOmnichannel.apiAudits.putById(connection, {
      id: "not-a-uuid" as any,
      body: update
    });
  });

  // 7. Malformed body: missing required fields
  const badUpdate: Partial<IApiAudit.IUpdate> = { endpoint: "/broken" };
  await TestValidator.httpError("Malformed body (missing required)")(400, 422)(async () => {
    await api.functional.apiOmnichannel.apiAudits.putById(connection, {
      id: auditId,
      body: badUpdate as any
    });
  });

  // 8. Disallowed/forbidden extra fields
  type IUpdateWithExtra = IApiAudit.IUpdate & { unauthorized_field: string };
  const extraUpdate: IUpdateWithExtra = {
    ...update,
    unauthorized_field: "disallowed"
  };
  await TestValidator.httpError("Body with forbidden fields rejected")(400, 422)(async () => {
    await api.functional.apiOmnichannel.apiAudits.putById(connection, {
      id: auditId,
      body: extraUpdate as any
    });
  });
}

