import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IIntegrationChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IIntegrationChannel";
import { IPageIIntegrationChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIIntegrationChannel";

export async function test_list_and_search_integration_channels_with_filtering(
  connection: api.IConnection
): Promise<void> {
  // 1. Create deterministic test records
  const timestamp = new Date().toISOString();
  const created: IIntegrationChannel[] = [];
  for (const [channel_code, channel_name, is_active] of [
    ["web", "WebPlatform", true],
    ["mobile", "MobileApp", false],
    ["partner_erp", "erp-Partner", true],
    ["console", "ConsoleApp", true],
  ] as const) {
    const record = await api.functional.apiOmnichannel.integrationChannels.post(
      connection,
      {
        body: {
          channel_code,
          channel_name,
        },
      }
    );
    typia.assert<IIntegrationChannel>(record);
    created.push(record);
  }

  // 2. Basic listing (no filters)
  const baseList = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    { body: {} }
  );
  typia.assert<IPageIIntegrationChannel>(baseList);
  TestValidator.predicate("listing contains created records")(() =>
    created.every((rec) => baseList.data.some((x) => x.id === rec.id))
  );

  // 3. Filter by channel_code
  const codeFilterList = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    { body: { filters: { channel_code: "web" } } }
  );
  typia.assert<IPageIIntegrationChannel>(codeFilterList);
  TestValidator.predicate("channel_code filter")(() =>
    codeFilterList.data.length === 1 && codeFilterList.data[0].channel_code === "web"
  );

  // 4. Filter by channel_name (partial)
  const nameFilterList = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    { body: { filters: { channel_name: "Mobile" } } }
  );
  typia.assert<IPageIIntegrationChannel>(nameFilterList);
  TestValidator.predicate("channel_name partial filter")(() =>
    nameFilterList.data.some((x) => x.channel_name.includes("Mobile"))
  );

  // 5. Filter by is_active
  const activeList = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    { body: { filters: { is_active: true } } }
  );
  typia.assert<IPageIIntegrationChannel>(activeList);
  TestValidator.predicate("is_active filter")(() =>
    activeList.data.every((x) => x.is_active === true)
  );

  // 6. Filter by created_at window (from - after 1970)
  const from = new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString(); // yesterday
  const dateFiltered = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    {
      body: { filters: { created_at: { from } } },
    }
  );
  typia.assert<IPageIIntegrationChannel>(dateFiltered);
  TestValidator.predicate("created_at window filter")(() =>
    dateFiltered.data.length >= 1
  );

  // 7. Sort by channel_name desc
  const sorted = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    {
      body: { sort: { by: "channel_name", order: "desc" } },
    }
  );
  typia.assert<IPageIIntegrationChannel>(sorted);
  TestValidator.predicate("sorted descending by channel_name")(() => {
    const arr = sorted.data.map((x) => x.channel_name);
    return arr.every((v, i, a) => i === 0 || a[i - 1] >= v);
  });

  // 8. Pagination: limit = 2
  const page1 = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    { body: { pagination: { page: 1, limit: 2 } } }
  );
  const page2 = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    { body: { pagination: { page: 2, limit: 2 } } }
  );
  typia.assert<IPageIIntegrationChannel>(page1);
  typia.assert<IPageIIntegrationChannel>(page2);
  TestValidator.predicate("pagination works")(() =>
    page1.data.length === 2 && page2.data.length >= 0
  );

  // 9. Out-of-range page requests (page >> total)
  const outOfRange = await api.functional.apiOmnichannel.integrationChannels.patch(
    connection,
    { body: { pagination: { page: 99, limit: 10 } } }
  );
  typia.assert<IPageIIntegrationChannel>(outOfRange);
  TestValidator.predicate("out-of-range pagination")(() =>
    outOfRange.data.length === 0
  );

  // 10. Invalid filter (created_at expects ISO string, pass number)
  await TestValidator.error("invalid filter triggers error")(() =>
    api.functional.apiOmnichannel.integrationChannels.patch(connection, {
      body: { filters: { created_at: { from: 12345 as any } }, },
    })
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IIntegrationChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IIntegrationChannel";
import typia, { tags } from "typia";

export async function test_retrieve_integration_channel_by_id_with_success_and_not_found_cases(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create a new integration channel
  const createInput: IIntegrationChannel.ICreate = {
    channel_code: `test_code_${Date.now()}`,
    channel_name: `Test Channel ${Date.now()}`,
  };
  const created = await api.functional.apiOmnichannel.integrationChannels.post(connection, {
    body: createInput,
  });
  typia.assert<IIntegrationChannel>(created);
  TestValidator.equals("created.channel_code")(createInput.channel_code)(created.channel_code);
  TestValidator.equals("created.channel_name")(createInput.channel_name)(created.channel_name);
  TestValidator.predicate("created.is_active should be boolean")(() => typeof created.is_active === "boolean");
  TestValidator.predicate("created.created_at should be string")(() => typeof created.created_at === "string");

  // Step 2: Retrieve by valid ID
  const retrieved = await api.functional.apiOmnichannel.integrationChannels.getById(connection, {
    id: created.id,
  });
  typia.assert<IIntegrationChannel>(retrieved);
  TestValidator.equals("retrieved == created", exceptDateFields)(created)(retrieved);

  // Step 3: Attempt to retrieve with invalid/non-existent UUID
  const invalidUuid: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("getById: not found should return 404 or business error")(404, 400, 422)(async () => {
    await api.functional.apiOmnichannel.integrationChannels.getById(connection, {
      id: invalidUuid,
    });
  });
}

function exceptDateFields(key: string): boolean {
  // Accepts precision differences in timestamp fields between creation and retrieval
  return key === "created_at" || key === "deleted_at";
}

