export async function test_update_integration_channel_metadata_and_handle_edge_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new integration channel for update testing.
  const createInput: IIntegrationChannel.ICreate = {
    channel_code: `testcode-${Math.random().toString(36).substr(2, 8)}`,
    channel_name: "Initial Channel Name",
  };

  const created = await api.functional.apiOmnichannel.integrationChannels.post(
    connection,
    { body: createInput },
  );
  typia.assert<IIntegrationChannel>(created);

  // 2. Update the channel name and is_active fields (Happy Path)
  const updateInput: IIntegrationChannel.IUpdate = {
    channel_name: "Updated Channel Name",
    is_active: false,
  };
  const updated = await api.functional.apiOmnichannel.integrationChannels.putById(
    connection,
    {
      id: created.id,
      body: updateInput,
    },
  );
  typia.assert<IIntegrationChannel>(updated);
  // Assert updated fields
  TestValidator.equals("channel_name updated")<string>(updateInput.channel_name!)(updated.channel_name);
  TestValidator.equals("is_active updated")<boolean>(updateInput.is_active!)(updated.is_active);
  // Assert immutable fields unchanged
  TestValidator.equals("id unchanged")<string>(created.id)(updated.id);
  TestValidator.equals("channel_code unchanged")<string>(created.channel_code)(updated.channel_code);
  TestValidator.equals("created_at unchanged")<string>(created.created_at)(updated.created_at);

  // 3. Attempt update with an invalid (random) ID
  await TestValidator.httpError("invalid UUID not found")
    (404, 400)(async () => {
      await api.functional.apiOmnichannel.integrationChannels.putById(
        connection,
        {
          id: typia.random<string & tags.Format<"uuid">>(),
          body: updateInput,
        },
      );
    });

  // 4. Attempt update with malformed payload (type mismatch)
  await TestValidator.httpError("malformed payload type")
    (400)(async () => {
      await api.functional.apiOmnichannel.integrationChannels.putById(
        connection,
        {
          id: created.id,
          body: { channel_name: 12345 as any }, // invalid type
        },
      );
    });

  // 5. Attempt update with missing/null ID
  // (a) Empty string
  await TestValidator.httpError("empty string id")
    (400)(async () => {
      await api.functional.apiOmnichannel.integrationChannels.putById(
        connection,
        {
          id: "" as any,
          body: updateInput,
        },
      );
    });
