import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IChannelSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IChannelSession";
import { IPageIChannelSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIChannelSession";

export async function test_list_and_search_channel_sessions_with_pagination(connection: api.IConnection): Promise<void> {
    // 1. Create test data: 3 different channel sessions
    const baseUserId = "6e01fa71-865b-41fd-84be-0d1a6750f4cc";
    const altUserId = "5d6e7c61-c321-48e9-bbe1-16d019c59b11";
    const integrationChannelA = "fadc7a22-68c3-44e9-89c0-1859b1479a74";
    const integrationChannelB = "6661a28c-6e39-44f2-9c0e-42c1c1f6ab0c";
    const apiTokenA = "cbde4585-8e9d-447f-81e5-98e57bfdfb87";
    const apiTokenB = "97ea5bfe-f553-47a4-9803-4bd0f0a29e1e";
    const now = new Date();

    const createPayloads: IChannelSession.ICreate[] = [
        {
            integration_channel_id: integrationChannelA,
            api_token_id: apiTokenA,
            user_id: baseUserId,
            session_key: "session-1-test",
            started_at: new Date(now.getTime() - 60000).toISOString(),
        },
        {
            integration_channel_id: integrationChannelA,
            api_token_id: apiTokenA,
            user_id: altUserId,
            session_key: "session-2-alt",
            started_at: new Date(now.getTime() - 30000).toISOString(),
        },
        {
            integration_channel_id: integrationChannelB,
            api_token_id: apiTokenB,
            user_id: null,
            session_key: "session-3-anon",
            started_at: now.toISOString(),
        },
    ];

    const createdSessions: IChannelSession[] = [];
    for (const payload of createPayloads) {
        const session = await api.functional.apiOmnichannel.channelSessions.post(connection, { body: payload });
        typia.assert<IChannelSession>(session);
        createdSessions.push(session);
    }

    // 2. Retrieve all (default, first page)
    const getAllRes = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
        body: {},
    });
    typia.assert<IPageIChannelSession>(getAllRes);
    TestValidator.predicate("all sessions include created")(() =>
        createdSessions.every(item => getAllRes.data.some(s => s.id === item.id))
    );

    // 3. Filter: user_id (baseUserId)
    const filterByUser = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
        body: {
            filters: { user_id: baseUserId }
        },
    });
    typia.assert<IPageIChannelSession>(filterByUser);
    TestValidator.predicate("user_id filter works")(() =>
        filterByUser.data.every(s => s.user_id === baseUserId)
    );

    // 4. Filter: integration_channel_id (integrationChannelB)
    const filterByChannel = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
        body: {
            filters: { integration_channel_id: integrationChannelB }
        },
    });
    typia.assert<IPageIChannelSession>(filterByChannel);
    TestValidator.predicate("integration_channel_id filter works")(() =>
        filterByChannel.data.every(s => s.integration_channel_id === integrationChannelB)
    );

    // 5. Pagination edge: limit 1, page 2
    const paginationEdge = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
        body: {
            pagination: {
                limit: 1,
                page: 2
            }
        },
    });
    typia.assert<IPageIChannelSession>(paginationEdge);
    TestValidator.equals("limit=1 returns 1 item")(
        1
    )(paginationEdge.data.length);
    TestValidator.equals("pagination meta - page 2")(
        2
    )(paginationEdge.pagination.current);

    // 6. Invalid input: negative page/limit
    await TestValidator.error("negative page triggers error")(
        async () => {
            await api.functional.apiOmnichannel.channelSessions.patch(connection, {
                body: { pagination: { limit: 1, page: -2 } },
            });
        }
    );
    await TestValidator.error("negative limit triggers error")(
        async () => {
            await api.functional.apiOmnichannel.channelSessions.patch(connection, {
                body: { pagination: { limit: -10, page: 1 } },
            });
        }
    );

    // 7. Out-of-bounds page: data = []
    const outOfBounds = await api.functional.apiOmnichannel.channelSessions.patch(connection, {
        body: { pagination: { page: 9999, limit: 1 } },
    });
    typia.assert<IPageIChannelSession>(outOfBounds);
    TestValidator.equals("empty data for far page")([] as IChannelSession[])(outOfBounds.data);
}

