import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IChannelSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IChannelSession";

export async function test_create_channel_session_with_valid_invalid_data(connection: api.IConnection): Promise<void> {
  // 1. Valid creation: Proper structure and data
  const validInput: IChannelSession.ICreate = {
    integration_channel_id: "3fa85f64-5717-4562-b3fc-2c963f66afa6",
    api_token_id: "123e4567-e89b-12d3-a456-426614174000",
    user_id: "b1a5071c-cfa6-4ca6-9d36-4296a81a1017",
    session_key: "unique-session-key-001",
    started_at: "2025-06-24T13:41:05.000Z",
  };
  const output = await api.functional.apiOmnichannel.channelSessions.post(connection, { body: validInput });
  typia.assert<IChannelSession>(output);
  TestValidator.equals("integration_channel_id")(validInput.integration_channel_id)(output.integration_channel_id);
  TestValidator.equals("api_token_id")(validInput.api_token_id)(output.api_token_id);
  TestValidator.equals("session_key")(validInput.session_key)(output.session_key);
  TestValidator.equals("started_at")(validInput.started_at)(output.started_at);
  if (validInput.user_id)
    TestValidator.equals("user_id")(validInput.user_id)(output.user_id!);

  // 2. Negative tests: Missing required fields
  const requiredFields: (keyof IChannelSession.ICreate)[] = [
    "integration_channel_id",
    "api_token_id",
    "session_key",
    "started_at",
  ];
  for (const missingField of requiredFields) {
