  const initialPayload: IChannelSession.ICreate = typia.random<IChannelSession.ICreate>();
  const created: IChannelSession = await api.functional.apiOmnichannel.channelSessions.post(connection, { body: initialPayload });
  typia.assert<IChannelSession>(created);

  // 2. Prepare a valid update (e.g., update user_id and ended_at)
  const newUserId: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  const endedAt: string & tags.Format<"date-time"> = new Date().toISOString() as string & tags.Format<"date-time">;
  const updatePayload: IChannelSession.IUpdate = {
    user_id: newUserId,
    ended_at: endedAt,
  };

  // 3. Update the session (allowed fields only)
  const updated: IChannelSession = await api.functional.apiOmnichannel.channelSessions.putById(connection, {
    id: created.id,
    body: updatePayload,
  });
