export async function test_update_webhook_event_metadata_and_handle_errors(
    connection: api.IConnection,
): Promise<void> {
    // 1. Create a webhook event (prerequisite for updates)
    const createInput: IWebhookEvent.ICreate = {
        integration_channel_id: typia.random<string & tags.Format<"uuid">>(),
        event_type: "order.updated",
        destination_url: "https://example.com/hook",
        event_hash: "abcdef1234567890",
    };
    const created: IWebhookEvent = await api.functional.apiOmnichannel.webhookEvents.post(connection, { body: createInput });
    typia.assert<IWebhookEvent>(created);

    // 2a. Valid update to delivery_status, attempt_count, delivered_at
    const updateInput: IWebhookEvent.IUpdate = {
        delivery_status: "success",
        attempt_count: (created.attempt_count || 0) + 1,
        delivered_at: new Date().toISOString(),
        last_attempted_at: new Date().toISOString(),
    };
    const updated: IWebhookEvent = await api.functional.apiOmnichannel.webhookEvents.putById(connection, {
        id: created.id,
        body: updateInput,
    });
    typia.assert<IWebhookEvent>(updated);
    TestValidator.equals("Updated delivery_status")<string>("success")(updated.delivery_status);
    TestValidator.equals("Updated attempt_count")<number>(updateInput.attempt_count!)(updated.attempt_count);
    TestValidator.equals("Updated delivered_at")<string | null>(updateInput.delivered_at!)(updated.delivered_at!);

    // 2b. Attempt update with forbidden/malformed payload - negative attempt_count
    const negativeAttemptInput: IWebhookEvent.IUpdate = {
        attempt_count: -1 as number & tags.Type<"int32">,
    };
    await TestValidator.httpError("Negative attempt_count should fail")(400)(() =>
        api.functional.apiOmnichannel.webhookEvents.putById(connection, {
            id: created.id,
            body: negativeAttemptInput,
        })
    );

    // 2c. Attempt update with invalid delivery_status
    const invalidStatusInput: any = {
        delivery_status: "invalid_status",
    };
    await TestValidator.httpError("Invalid delivery_status should fail")(400)(() =>
        api.functional.apiOmnichannel.webhookEvents.putById(connection, {
            id: created.id,
            body: invalidStatusInput,
        })
    );

    // 2d. Attempt update using invalid ID (random UUID not matching any event)
    const randomInvalidId = typia.random<string & tags.Format<"uuid">>();
    await TestValidator.httpError("Update with invalid ID should fail")(404)(() =>
        api.functional.apiOmnichannel.webhookEvents.putById(connection, {
            id: randomInvalidId,
            body: updateInput,
        })
    );

    // 2e. Attempt update with missing/empty ID (simulate as empty string)
    await TestValidator.httpError("Update with empty ID should fail")(400)(() =>
        api.functional.apiOmnichannel.webhookEvents.putById(connection, {
            id: "" as string & tags.Format<"uuid">,
            body: updateInput,
        })
