import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IAITrainingData } from "@ORGANIZATION/PROJECT-api/lib/structures/IAITrainingData";

export async function test_create_ai_training_dataset_with_valid_and_invalid_data(connection: api.IConnection): Promise<void> {
  // ---------- Step 1: Positive Test: Create with all valid data ----------
  const validInput: IAITrainingData.ICreate = {
    name: "UnitTest Dataset",
    version: "v1.0.0",
    source_uri: "s3://test-bucket/dataset/unittest.csv",
    schema_hash: "abc123schemahashforunittest",
    // Optional fields
    ai_model_id: null,
    record_count: 1000,
  };
  const created: IAITrainingData = await api.functional.advancedAI.aiTrainingData.post(connection, { body: validInput });
  typia.assert<IAITrainingData>(created);
  TestValidator.equals("created dataset, name")(validInput.name)(created.name);
  TestValidator.equals("created dataset, version")(validInput.version)(created.version);
  TestValidator.equals("created dataset, source_uri")(validInput.source_uri)(created.source_uri);
  TestValidator.equals("created dataset, schema_hash")(validInput.schema_hash)(created.schema_hash);
  TestValidator.equals("created dataset, record_count")(validInput.record_count)(created.record_count);
  TestValidator.equals("created dataset, ai_model_id")(validInput.ai_model_id)(created.ai_model_id);

  // ---------- Step 2: Negative Tests: Required field validation ----------
  const requiredFields: Array<keyof IAITrainingData.ICreate> = ["name", "version", "source_uri", "schema_hash"];
  for (const field of requiredFields) {
    const broken: IAITrainingData.ICreate = { ...validInput };
    // Delete the required field
    delete (broken as any)[field];
    await TestValidator.httpError(`missing: ${field}`)(400)(async () =>
      api.functional.advancedAI.aiTrainingData.post(connection, { body: broken })
    );
  }
  // Invalid type field: Give 'record_count' as string instead of number
  const invalidRecordCount = { ...validInput, record_count: "oops" as any };
  await TestValidator.httpError("invalid type: record_count")(400)(() =>
    api.functional.advancedAI.aiTrainingData.post(connection, { body: invalidRecordCount })
  );

  // ---------- Step 3: Duplicate entry test (unique constraint) ----------
  await TestValidator.httpError("duplicate entry should fail")(409)(async () =>
    api.functional.advancedAI.aiTrainingData.post(connection, { body: validInput })
  );
}

