/**
 * E2E Test: AI Training Data Soft Deletion & Invalid ID Handling
 *
 * - Successfully soft-deletes a valid AI training dataset (by ID)
 * - Ensures repeated delete or deletion of a non-existent ID is handled with error
 */
export async function test_delete_ai_training_dataset_and_handle_invalid_id(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create new AI training data
  const createInput: IAITrainingData.ICreate = {
    name: "Test Dataset E2E",
    version: "v1-e2e-20250624",
    source_uri: "s3://bucket/test-dataset-e2e.parquet",
    schema_hash: "b14faa2d42dca4283e2ea15e28e5c4a7",
    record_count: 4200,
    ai_model_id: null,
  };

  const created: IAITrainingData = await api.functional.advancedAI.aiTrainingData.post(connection, { body: createInput });
  typia.assert(created);
  TestValidator.predicate("created dataset matches input")(() => (
    created.name === createInput.name &&
    created.version === createInput.version &&
    created.source_uri === createInput.source_uri &&
    created.schema_hash === createInput.schema_hash &&
    created.record_count === createInput.record_count
  ));

  // 2. Soft delete with valid id
  const delResult: IOperationResult = await api.functional.advancedAI.aiTrainingData.eraseById(connection, { id: created.id });
