  await TestValidator.httpError("invalid ID delete (should be not found)")(404, 400, 409)(async () => {
    await api.functional.advancedAI.aiTrainingData.eraseById(connection, { id: invalidId });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IAIFeatureStore } from "@ORGANIZATION/PROJECT-api/lib/structures/IAIFeatureStore";
import { IPageIAIFeatureStore } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAIFeatureStore";

export async function test_list_ai_feature_store_with_filtering_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Wide search: No filters, basic pagination
  const wideSearchInput: IAIFeatureStore.IRequest = { page: 1, limit: 10 };
  const wideResult: IPageIAIFeatureStore = await api.functional.advancedAI.aiFeatureStore.patch(connection, { body: wideSearchInput });
  typia.assert(wideResult);
  TestValidator.predicate("wide search returns array")(Array.isArray(wideResult.data));

  // 2. If wide search returned results, pick a field for narrow filtering
  if (wideResult.data.length > 0) {
    const sampleFeature = wideResult.data[0];
    // Try filtering by name
    const filterByNameInput: IAIFeatureStore.IRequest = { name: sampleFeature.name, page: 1, limit: 10 };
    const nameResult = await api.functional.advancedAI.aiFeatureStore.patch(connection, { body: filterByNameInput });
    typia.assert(nameResult);
    TestValidator.predicate("name filtering returns all expected names")(
      nameResult.data.every(f => f.name === sampleFeature.name)
    );

    // Try filtering by ai_model_version_id
    const filterByModelInput: IAIFeatureStore.IRequest = { ai_model_version_id: sampleFeature.ai_model_version_id, page: 1, limit: 10 };
    const modelResult = await api.functional.advancedAI.aiFeatureStore.patch(connection, { body: filterByModelInput });
    typia.assert(modelResult);
    TestValidator.predicate("model filtering returns all expected models")(
      modelResult.data.every(f => f.ai_model_version_id === sampleFeature.ai_model_version_id)
    );
  }

  // 3. Out-of-range pagination (page far beyond available pages): expect empty results
  const outOfRangeInput: IAIFeatureStore.IRequest = { page: 1000, limit: 10 };
  const outOfRangeResult = await api.functional.advancedAI.aiFeatureStore.patch(connection, { body: outOfRangeInput });
  typia.assert(outOfRangeResult);
  TestValidator.equals("empty page", (key) => key === "pagination")([] as IAIFeatureStore[])(outOfRangeResult.data);
  // Pagination structure still valid
  TestValidator.predicate("pagination fields valid")(
    typeof outOfRangeResult.pagination.current === "number" &&
    typeof outOfRangeResult.pagination.limit === "number" &&
    typeof outOfRangeResult.pagination.pages === "number" &&
    typeof outOfRangeResult.pagination.records === "number"
  );
}

