export async function test_list_ai_decision_logs_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Baseline: Retrieve all with no filters
  const baseRes = await api.functional.advancedAI.aiDecisionLogs.patch(connection, {
    body: {},
  });
  typia.assert<IPageIAIDecisionLog>(baseRes);
  TestValidator.predicate("pagination object")(!!baseRes.pagination);
  TestValidator.predicate("data array")(Array.isArray(baseRes.data));

  // Store available filters for further test cases (if present)
  const firstLog = baseRes.data[0];

  // 2. Filter by ai_model_version_id (if data available)
  if (firstLog) {
    const filterRes = await api.functional.advancedAI.aiDecisionLogs.patch(connection, {
      body: { ai_model_version_id: firstLog.ai_model_version_id },
    });
    typia.assert<IPageIAIDecisionLog>(filterRes);
    for (const log of filterRes.data)
      TestValidator.equals("ai_model_version_id matches")<string>(firstLog.ai_model_version_id)(log.ai_model_version_id);
  }

  // 3. Filter by target_entity (if data available)
  if (firstLog) {
    const filterRes = await api.functional.advancedAI.aiDecisionLogs.patch(connection, {
      body: { target_entity: firstLog.target_entity },
    });
    typia.assert<IPageIAIDecisionLog>(filterRes);
    for (const log of filterRes.data)
      TestValidator.equals("target_entity matches")<string>(firstLog.target_entity)(log.target_entity);
  }

  // 4. Sorting by created_at desc/asc
  const sortDescRes = await api.functional.advancedAI.aiDecisionLogs.patch(connection, {
    body: { order_by: "created_at", order: "desc", limit: 5 },
  });
  typia.assert<IPageIAIDecisionLog>(sortDescRes);
  const descData = sortDescRes.data;
  if (descData.length > 1)
    TestValidator.predicate("descending order")(descData.every((a,idx,arr) => !idx || a.created_at <= arr[idx-1].created_at));

  const sortAscRes = await api.functional.advancedAI.aiDecisionLogs.patch(connection, {
    body: { order_by: "created_at", order: "asc", limit: 5 },
  });
  typia.assert<IPageIAIDecisionLog>(sortAscRes);
  const ascData = sortAscRes.data;
  if (ascData.length > 1)
    TestValidator.predicate("ascending order")(ascData.every((a,idx,arr) => !idx || a.created_at >= arr[idx-1].created_at));

  // 5. Pagination: limit and multiple pages
  const limit = 2;
  const page1 = await api.functional.advancedAI.aiDecisionLogs.patch(connection, {
    body: { limit, page: 1, order_by: "created_at" },
  });
  typia.assert<IPageIAIDecisionLog>(page1);
  const page2 = await api.functional.advancedAI.aiDecisionLogs.patch(connection, {
    body: { limit, page: 2, order_by: "created_at" },
  });
  typia.assert<IPageIAIDecisionLog>(page2);

  // Ensure no duplicate IDs between page1 and page2
  const ids1 = new Set(page1.data.map(x => x.id));
  for (const log of page2.data) {
    TestValidator.predicate("paginate no duplicates")( !ids1.has(log.id) );
  }

  // 6. Edge: Out-of-bounds high page
  const outRes = await api.functional.advancedAI.aiDecisionLogs.patch(connection, {
    body: { limit: 5, page: 10000 },
  });
  typia.assert<IPageIAIDecisionLog>(outRes);
  TestValidator.equals("out-of-bounds page empty")(0)(outRes.data.length);

  // 7. Negative: Invalid/malformed filter value
  await TestValidator.error("malformed UUID filter")(() =>
    api.functional.advancedAI.aiDecisionLogs.patch(connection, {
      body: { ai_model_version_id: "not-a-uuid" as any },
    }),
  );
}

