import typia, { tags } from "typia";

// Main scenario function
export async function test_update_ai_decision_log_by_id_with_various_conditions(
  connection: api.IConnection
): Promise<void> {
  // 1. Create a valid AI decision log
  const createInput: IAIDecisionLog.ICreate = {
    ai_model_version_id: typia.random<string & tags.Format<"uuid">>(),
    target_entity: "order",
    target_entity_id: typia.random<string & tags.Format<"uuid">>(),
    decision: "recommend",
    input_snapshot: "{\"sample\":true}",
    rationale: "Initial rationale for audit.",
    target_user_id: typia.random<string & tags.Format<"uuid">>(),
  };
  const created: IAIDecisionLog = await api.functional.advancedAI.aiDecisionLogs.post(connection, { body: createInput });
  typia.assert<IAIDecisionLog>(created);

  // 2. Happy path: update rationale
  const updateInput: IAIDecisionLog.IUpdate = { rationale: "Updated rationale for audit log." };
  const updated: IAIDecisionLog = await api.functional.advancedAI.aiDecisionLogs.putById(connection, {
    id: created.id,
    body: updateInput,
  });
  typia.assert<IAIDecisionLog>(updated);
  TestValidator.equals("updated rationale matches")(updateInput.rationale)(updated.rationale);
  // All other fields should remain the same
  TestValidator.equals("id unchanged")(created.id)(updated.id);
  TestValidator.equals("ai_model_version_id unchanged")(created.ai_model_version_id)(updated.ai_model_version_id);
  TestValidator.equals("target_user_id unchanged")(created.target_user_id)(updated.target_user_id);
  TestValidator.equals("target_entity unchanged")(created.target_entity)(updated.target_entity);
  TestValidator.equals("target_entity_id unchanged")(created.target_entity_id)(updated.target_entity_id);
  TestValidator.equals("decision unchanged")(created.decision)(updated.decision);
  TestValidator.equals("input_snapshot unchanged")(created.input_snapshot)(updated.input_snapshot);
  TestValidator.equals("created_at unchanged")(created.created_at)(updated.created_at);

  // 3. Negative: Update with invalid/nonexistent ID
  await TestValidator.httpError("should fail on nonexistent ID")(404)(async () =>
    api.functional.advancedAI.aiDecisionLogs.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(), // very likely not present
      body: { rationale: "Irrelevant." },
    })
  );

  // 4. Negative: Update with missing required fields, such as an empty object
  await TestValidator.httpError("should fail with invalid body")(400, 422)(async () =>
    api.functional.advancedAI.aiDecisionLogs.putById(connection, {
      id: created.id,
      body: {}, // missing rationale, violates contract
    } as any)
  );

  // 5. Negative: Attempt to update immutable fields
  const badUpdate = {
    rationale: "Trying immutable mutation",
    decision: "blocked", // decision is NOT updatable
    ai_model_version_id: typia.random<string & tags.Format<"uuid">>(),
  } as any;
  await TestValidator.httpError("should fail on immutable field mutation")(400, 422)(async () =>
    api.functional.advancedAI.aiDecisionLogs.putById(connection, {
      id: created.id,
      body: badUpdate,
    })
  );
}

