/**
 * Test deletion of AI decision log by id, and proper handling of not_found cases.
 */
export async function test_delete_ai_decision_log_by_id_and_handle_not_found(
  connection: api.IConnection
): Promise<void> {
  // 1. Create an AI decision log
  const createInput: IAIDecisionLog.ICreate = typia.random<IAIDecisionLog.ICreate>();
  const log: IAIDecisionLog = await api.functional.advancedAI.aiDecisionLogs.post(connection, { body: createInput });
  typia.assert<IAIDecisionLog>(log);

  // 2. Delete the created log
  const delResult: IDeletionResult = await api.functional.advancedAI.aiDecisionLogs.eraseById(connection, { id: log.id });
  typia.assert<IDeletionResult>(delResult);
  TestValidator.equals("deleted id matches")(log.id)(delResult.id);
  TestValidator.predicate("status deleted")(() => delResult.status === "deleted" || delResult.status === "archived");

  // 3. Attempt to delete the same log again (should return not_found or error)
  const delResultRepeat: IDeletionResult = await api.functional.advancedAI.aiDecisionLogs.eraseById(connection, { id: log.id });
  typia.assert<IDeletionResult>(delResultRepeat);
  TestValidator.predicate("status not_found or error")(
    () => delResultRepeat.status === "not_found" || delResultRepeat.status === "deleted" || delResultRepeat.status === "archived"
  );

  // 4. Attempt to delete a completely random (non-existing) id
  const randomId: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  const delResultRandom: IDeletionResult = await api.functional.advancedAI.aiDecisionLogs.eraseById(connection, { id: randomId });
