export async function test_list_ai_feedback_records_with_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Default listing (no filters)
  const base: IPageIAiFeedback = await api.functional.advancedAI.aiFeedback.patch(connection, {
    body: {},
  });
  typia.assert<IPageIAiFeedback>(base);
  TestValidator.predicate("data array defined")(!!base.data);
  TestValidator.predicate("pagination defined")(!!base.pagination);
  TestValidator.predicate("current page is 1")(base.pagination.current === 1);

  // if there's data, pick sample values for followup filters
  let sampleFeedback: IAiFeedback | undefined = base.data[0];

  // 2. Filter by user_id, feedback_type, decision_log_id, time range
  if (sampleFeedback) {
    // Prepare tight filter (should match exactly one if unique keys are used)
    const resFiltered = await api.functional.advancedAI.aiFeedback.patch(connection, {
      body: {
        user_id: sampleFeedback.user_id ?? undefined,
        feedback_type: sampleFeedback.feedback_type,
        decision_log_id: sampleFeedback.decision_log_id,
        time_range_start: sampleFeedback.created_at,
        time_range_end: sampleFeedback.created_at,
        limit: 1,
        page: 1,
        sort_order: "asc",
      },
    });
    typia.assert<IPageIAiFeedback>(resFiltered);
    if (resFiltered.data.length > 0) {
      TestValidator.equals("filtered feedback matches user_id")(
        sampleFeedback.user_id,
      )(resFiltered.data[0].user_id);
      TestValidator.equals("filtered feedback matches feedback_type")(
        sampleFeedback.feedback_type,
      )(resFiltered.data[0].feedback_type);
      TestValidator.equals("filtered feedback matches decision_log_id")(
        sampleFeedback.decision_log_id,
      )(resFiltered.data[0].decision_log_id);
    }
    TestValidator.predicate("filtered result has valid pagination")(
      !!resFiltered.pagination,
    );
  }

  // 3. Paginate to next page (should work for multiple pages)
  if (base.pagination.pages > 1) {
    const resPage2 = await api.functional.advancedAI.aiFeedback.patch(connection, {
      body: {
        page: 2,
        limit: base.pagination.limit,
      },
    });
    typia.assert<IPageIAiFeedback>(resPage2);
    TestValidator.equals("pagination page updated")(2)(resPage2.pagination.current);
    // Data array may be empty if last page
    TestValidator.predicate("pagination presence")(!!resPage2.pagination);
  }

  // 4. Non-existing user_id (should return empty array)
  await (async () => {
    const nonExistUserId = "00000000-0000-0000-0000-000000000000";
    const resNone = await api.functional.advancedAI.aiFeedback.patch(connection, {
      body: { user_id: nonExistUserId },
    });
    typia.assert<IPageIAiFeedback>(resNone);
    TestValidator.equals("no feedback for non-exist user")<IAiFeedback[]>([])(resNone.data);
  })();

  // 5. Excessive page (expect empty data)
  await (async () => {
    const veryHighPage = 999_999;
    const resPage = await api.functional.advancedAI.aiFeedback.patch(connection, {
      body: { page: veryHighPage },
    });
    typia.assert<IPageIAiFeedback>(resPage);
    TestValidator.equals("empty result for out-of-range page")<IAiFeedback[]>([])(resPage.data);
  })();

  // 6. Excessive limit (system should cap or error)
  await TestValidator.httpError("system enforces max limit or error")(400, 422)(async () => {
    await api.functional.advancedAI.aiFeedback.patch(connection, {
      body: { limit: 999_999 },
    });
  });

  // 7. Invalid UUID in filter (bad format string)
  await TestValidator.httpError("invalid UUID filter yields error")(400, 422)(async () => {
    await api.functional.advancedAI.aiFeedback.patch(connection, {
      body: { user_id: "not-a-uuid" },
    });
  });
}

