import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { ISecurityEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/ISecurityEvent";
import { IPageISecurityEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageISecurityEvent";

export async function test_list_security_event_logs_with_filter_and_pagination(
  connection: api.IConnection
): Promise<void> {
  // 1. Retrieve all security event logs (no filter)
  const allResults = await api.functional.securityCompliance.securityEvent.patch(connection, { body: {} });
  typia.assert<IPageISecurityEvent>(allResults);
  TestValidator.predicate("pagination in allResults")(allResults.pagination !== undefined);
  TestValidator.predicate("array in allResults")(Array.isArray(allResults.data));

  // 2. Try to get some sample event_type and user_id for filtering
  const sampleEvent = allResults.data[0];
  const eventType = sampleEvent?.event_type;
  const userId = sampleEvent?.user_id;
  const createdAt = sampleEvent?.created_at;

  // 3. Filter by event_type
  if (eventType) {
    const eventTypeResults = await api.functional.securityCompliance.securityEvent.patch(connection, {
      body: { event_type: eventType }
    });
    typia.assert<IPageISecurityEvent>(eventTypeResults);
    TestValidator.predicate("all event_type equal")(
      eventTypeResults.data.every(ev => ev.event_type === eventType)
    );
  }

  // 4. Filter by user_id (if user id present)
  if (userId) {
    const userResults = await api.functional.securityCompliance.securityEvent.patch(connection, {
      body: { user_id: userId }
    });
    typia.assert<IPageISecurityEvent>(userResults);
    TestValidator.predicate("all user_id equal")(
      userResults.data.every(ev => ev.user_id === userId)
    );
  }

  // 5. Filter by date range
  if (createdAt) {
    const fromTime = createdAt;
    const toTime = createdAt;
    const dateRangeResults = await api.functional.securityCompliance.securityEvent.patch(connection, {
      body: { from_time: fromTime, to_time: toTime }
    });
    typia.assert<IPageISecurityEvent>(dateRangeResults);
    TestValidator.predicate("all within date range")(
      dateRangeResults.data.every(ev => ev.created_at >= fromTime && ev.created_at <= toTime)
    );
  }

  // 6. Pagination: limit and page
  const limit = 2;
  const page = 1;
  const paginationResults = await api.functional.securityCompliance.securityEvent.patch(connection, {
    body: { limit, page }
  });
  typia.assert<IPageISecurityEvent>(paginationResults);
  TestValidator.predicate("data array size <= limit")(
    paginationResults.data.length <= limit
  );
  TestValidator.predicate("pagination current matches")(
    paginationResults.pagination.current === page
  );

  // 7. Out-of-range page: pick a page number much greater than total pages
  const outOfRangeResults = await api.functional.securityCompliance.securityEvent.patch(connection, {
    body: { page: 99999, limit: 2 }
  });
  typia.assert<IPageISecurityEvent>(outOfRangeResults);
  TestValidator.equals("no results in out-of-range page")(
    [] as ISecurityEvent[]
  )(outOfRangeResults.data);

  // 8. Invalid filter (nonsense event_type guaranteed not present)
  const nonsenseTypeResults = await api.functional.securityCompliance.securityEvent.patch(connection, {
    body: { event_type: "__NONE_EXISTING_EVENT_TYPE__" }
  });
  typia.assert<IPageISecurityEvent>(nonsenseTypeResults);
  TestValidator.equals("nonsense filter yields empty array")(
    [] as ISecurityEvent[]
  )(nonsenseTypeResults.data);
}

