import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ISecurityEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/ISecurityEvent";
import typia from "typia";

export async function test_create_security_event_log_with_valid_and_invalid_data(connection: api.IConnection): Promise<void> {
  // 1. Positive Case: Create with valid data
  const validInput: ISecurityEvent.ICreate = {
    event_type: "login_attempt",
    event_details: JSON.stringify({ username: "testuser", result: "success" }),
    // user_id/session_id/api_client_id are optional/null
  };

  const created = await api.functional.securityCompliance.securityEvent.post(connection, { body: validInput });
  typia.assert<ISecurityEvent>(created);
  TestValidator.equals("event_type")(validInput.event_type)(created.event_type);
  TestValidator.equals("event_details")(validInput.event_details)(created.event_details);
  TestValidator.predicate("has id field")(() => typeof created.id === "string" && created.id.length > 0);
  TestValidator.predicate("has created_at field")(() => typeof created.created_at === "string" && created.created_at.length > 0);

  // 2. Negative Case: Missing required field (event_type)
  const missingEventType: ISecurityEvent.ICreate = {
    // @ts-expect-error event_type is missing intentionally for the negative test
    event_details: "{}"
  };
  await TestValidator.httpError("missing event_type")(400)(async () => {
    // Type assertion is present, but runtime API should reject
    await api.functional.securityCompliance.securityEvent.post(connection, { body: missingEventType as any });
  });

  // 3. Negative Case: Missing required field (event_details)
  const missingEventDetails: ISecurityEvent.ICreate = {
    event_type: "token_rotation"
    // event_details missing
  };
  await TestValidator.httpError("missing event_details")(400)(async () => {
    await api.functional.securityCompliance.securityEvent.post(connection, { body: missingEventDetails as any });
  });

  // 4. Negative Case: Invalid event_type type (number instead of string)
  const invalidEventType: any = {
    event_type: 123,
    event_details: "Some details"
  };
  await TestValidator.httpError("invalid event_type type")(400)(async () => {
    await api.functional.securityCompliance.securityEvent.post(connection, { body: invalidEventType });
  });

  // 5. Negative Case: Invalid event_details type (number instead of string)
  const invalidEventDetails: any = {
    event_type: "privilege_escalation",
    event_details: 789
  };
  await TestValidator.httpError("invalid event_details type")(400)(async () => {
    await api.functional.securityCompliance.securityEvent.post(connection, { body: invalidEventDetails });
  });

  // 6. (Optional) Duplicate record
  // If unique constraint is enforced (though typically security audit events have no strict uniqueness),
  // try creating identical event twice and expect either a success (audit logs are append-only) or a specific error
  const duplicateInput: ISecurityEvent.ICreate = {
    event_type: "duplicate_test",
    event_details: JSON.stringify({ attempt: 1 })
  };
  const first = await api.functional.securityCompliance.securityEvent.post(connection, { body: duplicateInput });
  typia.assert<ISecurityEvent>(first);
  // Try again
  let duplicatedOk = false;
  try {
    const second = await api.functional.securityCompliance.securityEvent.post(connection, { body: duplicateInput });
    typia.assert<ISecurityEvent>(second);
    duplicatedOk = true;
  } catch (err) {
    // Accept both: error (uniqueness enforced) or success (append-only)
    duplicatedOk = true;
  }
  TestValidator.predicate("duplicate event allowed or handled error")(() => duplicatedOk === true);
}

