export async function test_list_and_search_permission_records_with_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. Positive: Basic search, no filters
  {
    const response = await api.functional.securityCompliance.permission.patch(
      connection,
      { body: {} },
    );
    typia.assert<IPageIPermission>(response);
    TestValidator.predicate("should return data array")(
      () => Array.isArray(response.data),
    );
    for (const perm of response.data) {
      typia.assert<IPermission>(perm);
    }
  }

  // 2. Positive: Filter by code (unlikely value)
  {
    const code = "unlikely_code_name_xyz123";
    const response = await api.functional.securityCompliance.permission.patch(
      connection,
      { body: { code } },
    );
    typia.assert<IPageIPermission>(response);
    TestValidator.equals("Empty result for unknown code")([] as IPermission[]) (
      response.data,
    );
  }

  // 3. Positive: Filter by description (empty match)
  {
    const desc = "%%%impossible%%%%";
    const response = await api.functional.securityCompliance.permission.patch(
      connection,
      { body: { description: desc } },
    );
    typia.assert<IPageIPermission>(response);
    TestValidator.equals("Empty array for unmatched description")([] as IPermission[])(
      response.data,
    );
  }

  // 4. Positive: Pagination logic, requesting page > 1
  {
    const response = await api.functional.securityCompliance.permission.patch(
      connection,
      { body: { page: 2, limit: 2 } },
    );
    typia.assert<IPageIPermission>(response);
    TestValidator.predicate("Data is array")(() => Array.isArray(response.data));
    TestValidator.predicate("On page 2")(
      () => response.pagination.current === 2,
    );
  }

  // 5. Positive: Sorting by code (asc)
  {
    const response = await api.functional.securityCompliance.permission.patch(
      connection,
      { body: { sort_field: "code", sort_direction: "asc", limit: 5 } },
    );
    typia.assert<IPageIPermission>(response);
    if (response.data.length > 1) {
      for (let i = 1; i < response.data.length; i++) {
        TestValidator.predicate(`Result is sorted by code [${i}]`)(
          () =>
            response.data[i - 1].code.localeCompare(response.data[i].code) <= 0,
        );
      }
    }
  }

  // 6. Negative: Invalid page (negative)
  {
    await TestValidator.httpError("Negative page returns error")(400)(async () =>
      api.functional.securityCompliance.permission.patch(connection, {
        body: { page: -1 },
      }),
    );
  }

  // 7. Negative: Invalid limit (zero)
  {
    await TestValidator.httpError("Zero limit returns error")(400)(async () =>
      api.functional.securityCompliance.permission.patch(connection, {
        body: { limit: 0 },
      }),
    );
  }

  // 8. Negative: Impossible date range
  {
    const from = "2023-01-01T00:00:00.000Z";
    const to = "2022-01-01T00:00:00.000Z";
    const response = await api.functional.securityCompliance.permission.patch(
      connection,
      { body: { created_from: from, created_to: to } },
    );
    typia.assert<IPageIPermission>(response);
    TestValidator.equals("Empty array for impossible date range")([] as IPermission[])(
      response.data,
    );
  }

  // 9. Negative: Invalid sort_field (should not throw error)
  {
    const response = await api.functional.securityCompliance.permission.patch(
      connection,
      { body: { sort_field: "not_a_field", limit: 3 } },
    );
    typia.assert<IPageIPermission>(response);
    TestValidator.predicate("Data is array")(
      () => Array.isArray(response.data),
    );
  }
}

