export async function test_update_locale_with_various_payloads(connection: api.IConnection): Promise<void> {
  // 1. Create a new locale entry to update
  const localeCreateA: ICoreLocale.ICreate = {
    code: "en-US",
    name: "English (United States)",
  };
  const createdA = await api.functional.core.locales.post(connection, { body: localeCreateA });
  typia.assert<ICoreLocale>(createdA);

  // 2. Update locale's code and name; verify update
  const updatePayload: ICoreLocale.IUpdate = {
    code: "en-GB",
    name: "English (United Kingdom)",
  };
  const updated = await api.functional.core.locales.putById(connection, { id: createdA.id, body: updatePayload });
  typia.assert<ICoreLocale>(updated);
  TestValidator.equals("Update changed code", undefined)(updatePayload.code)(updated.code);
  TestValidator.equals("Update changed name", undefined)(updatePayload.name)(updated.name);

  // 3. Try updating with invalid (empty) code
