import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICoreConsent } from "@ORGANIZATION/PROJECT-api/lib/structures/ICoreConsent";
import { IPageICoreConsent } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICoreConsent";
import typia from "typia";

export async function test_list_and_search_consents_with_various_filters_and_pagination(connection: api.IConnection): Promise<void> {
  // 1. POST at least one consent
  const baseConsentData: ICoreConsent.ICreate = {
    user_id: typia.random<"string & typia.tags.Format<'uuid'>">(),
    type: "ai_training",
    is_granted: true,
    granted_at: new Date().toISOString(),
    revoked_at: null,
  };
  const consent = await api.functional.core.consents.post(connection, { body: baseConsentData });
  typia.assert<ICoreConsent>(consent);

  // 2. List with no filters (default pagination)
  const listAll = await api.functional.core.consents.patch(connection, { body: {} });
  typia.assert<IPageICoreConsent>(listAll);
  TestValidator.predicate("listAll contains created consent")(
    listAll.data.some((c) => c.id === consent.id)
  );

  // 3. List filtered by user_id
  const listByUser = await api.functional.core.consents.patch(connection, { body: { filter: { user_id: baseConsentData.user_id } } });
  typia.assert<IPageICoreConsent>(listByUser);
  TestValidator.predicate("all user_id match filter")(
    listByUser.data.every((c) => c.user_id === baseConsentData.user_id)
  );

  // 4. Search by type substring
  const searchType = await api.functional.core.consents.patch(connection, { body: { search: "ai_" } });
  typia.assert<IPageICoreConsent>(searchType);
  TestValidator.predicate("searchType includes 'ai_' types")(
    searchType.data.some((c) => c.type.includes("ai_"))
  );

  // 5. Pagination: small per-page, last page yields empty or last entries
  const createdIds: string[] = [consent.id];
  for (let i = 0; i < 7; ++i) {
    const dj = await api.functional.core.consents.post(connection, {
      body: {
        ...baseConsentData,
        type: `ai_purpose_${i}`,
        granted_at: new Date(Date.now() + 1000 * (i + 1)).toISOString(),
      },
    });
    typia.assert<ICoreConsent>(dj);
    createdIds.push(dj.id);
  }
  // Query with limit 3, page 1
  const page1 = await api.functional.core.consents.patch(connection, { body: { limit: 3, page: 1, sort: "granted_at:asc" } });
  typia.assert<IPageICoreConsent>(page1);
  TestValidator.predicate("page 1 has <= 3 items")(
    page1.data.length <= 3
  );
  // Query far beyond available pages
  const invalidPage = await api.functional.core.consents.patch(connection, { body: { limit: 3, page: 10 } });
  typia.assert<IPageICoreConsent>(invalidPage);
  TestValidator.predicate("empty data or last results on overflow page")(
    Array.isArray(invalidPage.data)
  );

  // 6. is_granted status filter (true/false)
  const revoked: ICoreConsent.ICreate = {
    ...baseConsentData,
    type: "data_sharing",
    is_granted: false,
    granted_at: new Date().toISOString(),
    revoked_at: new Date().toISOString(),
  };
  const revokedConsent = await api.functional.core.consents.post(connection, { body: revoked });
  typia.assert<ICoreConsent>(revokedConsent);
  const listRevoked = await api.functional.core.consents.patch(connection, { body: { filter: { is_granted: false } } });
  typia.assert<IPageICoreConsent>(listRevoked);
  TestValidator.predicate("revoked consents have is_granted === false")(
    listRevoked.data.every((c) => c.is_granted === false)
  );

  // 7. Edge: nonsense user_id (should return empty set)
  const nonsenseUser = await api.functional.core.consents.patch(connection, { body: { filter: { user_id: "00000000-0000-0000-0000-000000000999" } } });
  typia.assert<IPageICoreConsent>(nonsenseUser);
  TestValidator.equals("nonsense user_id returns empty", () => false)([] as ICoreConsent[])(nonsenseUser.data);

  // 8. Edge: negative page number (should handle gracefully)
  await TestValidator.error("negative page returns error or empty")(
    async () => {
      await api.functional.core.consents.patch(connection, { body: { page: -1 } });
    }
  );

  // 9. Edge: invalid filter key (should handle gracefully)
  await TestValidator.error("invalid filter key")(
    async () => {
      await api.functional.core.consents.patch(connection, { body: { filter: { not_a_real_field: "nonsense" } as any } });
    }
  );
}

