import api from "@ORGANIZATION/PROJECT-api";
import { ICoreConsent } from "@ORGANIZATION/PROJECT-api/lib/structures/ICoreConsent";
import typia from "typia";

export async function test_create_consent_with_valid_and_invalid_payloads(connection: api.IConnection): Promise<void> {
  // 1. Happy Path: Valid consent creation
  const validUserId = typia.random<string & typia.tags.Format<"uuid">>();
  const now = new Date().toISOString();
  const consentInput: ICoreConsent.ICreate = {
    user_id: validUserId,
    type: "ai_training",
    is_granted: true,
    granted_at: now,
    revoked_at: null,
  };

  const created = await api.functional.core.consents.post(connection, { body: consentInput });
  typia.assert<ICoreConsent>(created);
  TestValidator.equals("user_id matches")(consentInput.user_id)(created.user_id);
  TestValidator.equals("type matches")(consentInput.type)(created.type);
  TestValidator.equals("is_granted matches")(consentInput.is_granted)(created.is_granted);
  TestValidator.equals("granted_at matches")(consentInput.granted_at)(created.granted_at);
  TestValidator.equals("revoked_at matches")(consentInput.revoked_at)(created.revoked_at);

  // 2. Negative Tests: Required fields missing
  await TestValidator.httpError("missing user_id")(400, 422)(async () =>
    api.functional.core.consents.post(connection, { body: { ...consentInput, user_id: undefined as any } })
  );
  await TestValidator.httpError("missing type")(400, 422)(async () =>
    api.functional.core.consents.post(connection, { body: { ...consentInput, type: undefined as any } })
  );
  await TestValidator.httpError("missing is_granted")(400, 422)(async () =>
    api.functional.core.consents.post(connection, { body: { ...consentInput, is_granted: undefined as any } })
  );
  await TestValidator.httpError("missing granted_at")(400, 422)(async () =>
    api.functional.core.consents.post(connection, { body: { ...consentInput, granted_at: undefined as any } })
  );

  // 2b. Malformed values
  await TestValidator.httpError("invalid user_id uuid")(400, 422)(async () =>
    api.functional.core.consents.post(connection, { body: { ...consentInput, user_id: "not-a-uuid" as any } })
  );
  await TestValidator.httpError("invalid granted_at format")(400, 422)(async () =>
    api.functional.core.consents.post(connection, { body: { ...consentInput, granted_at: "not-a-date" as any } })
  );
  await TestValidator.httpError("non-boolean is_granted")(400, 422)(async () =>
    api.functional.core.consents.post(connection, { body: { ...consentInput, is_granted: "yes" as any } })
  );

  // 3. Uniqueness & Conflict: Duplicate consent for same (user_id, type)
  await TestValidator.httpError("duplicate consent: conflict")(409)(async () =>
    api.functional.core.consents.post(connection, { body: consentInput })
  );

  // 4. User linkage/foreign user_id (assumes system rejects if foreign key is enforced)
  const fakeUserId = typia.random<string & typia.tags.Format<"uuid">>();
  if (fakeUserId !== validUserId) {
    await TestValidator.httpError("foreign (nonexistent) user_id")(400, 404, 409)(async () =>
      api.functional.core.consents.post(connection, {
        body: { ...consentInput, user_id: fakeUserId }
      })
    );
  } else {
    // In rare case, re-generate a new uuid if random collision
  }
}

