): Promise<void> {
  // Step 1: Create two distinct access log entries
  const now = new Date().toISOString();

  const log1Input: ICoreAccessLog.ICreate = {
    user_id: "11111111-1111-4111-8111-111111111111",
    session_id: "22222222-2222-4222-8222-222222222222",
    event_type: "login",
    ip: "192.168.0.1",
    agent: "TestAgent/1.0",
    success: true,
    occurred_at: now,
  };
  const log2Input: ICoreAccessLog.ICreate = {
    user_id: "33333333-3333-4333-8333-333333333333",
    session_id: "44444444-4444-4444-8444-444444444444",
    event_type: "logout",
    ip: "10.1.2.3",
