  };
  const created = await api.functional.core.accessLogs.post(connection, { body: validInput });
  typia.assert<ICoreAccessLog>(created);
  TestValidator.equals("log reflects evidence inputs except id", (k) => k === "id")(validInput)(created);

  // 2. Negative test - missing required fields
  const requiredFields: (keyof ICoreAccessLog.ICreate)[] = ["user_id", "event_type", "ip", "agent", "success", "occurred_at"];
