import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IProductCatalogProduct } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProduct";
import { IPageIProductCatalogProduct } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIProductCatalogProduct";
import typia from "typia";

export async function test_search_and_list_products_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. PRECONDITION: Create sample products
  const createProduct = async (input: IProductCatalogProduct.ICreate) =>
    await api.functional.productCatalog.products.post(connection, { body: input });

  const baseUserId = "11111111-1111-1111-1111-111111111111";
  const products: IProductCatalogProduct.ICreate[] = [
    {
      created_by_user_id: baseUserId,
      updated_by_user_id: baseUserId,
      name: "Test Product Alpha",
      description: "Alpha Description",
      internal_code: "ALPHA-001",
      status: "active",
    },
    {
      created_by_user_id: baseUserId,
      updated_by_user_id: baseUserId,
      name: "Test Product Beta",
      description: "Beta Desc",
      internal_code: "BETA-123",
      status: "draft",
    },
    {
      created_by_user_id: baseUserId,
      updated_by_user_id: baseUserId,
      name: "Gamma Device",
      description: "Gamma Desc",
      internal_code: "GAMMA-400",
      status: "archived",
    },
  ];
  const createdProducts: IProductCatalogProduct[] = [];
  for (const productInput of products) {
    const prod = await createProduct(productInput);
    createdProducts.push(prod);
  }

  // 2. LIST all (no filter)
  const allResult = await api.functional.productCatalog.products.patch(connection, {
    body: {},
  });
  typia.assert<IPageIProductCatalogProduct>(allResult);
  TestValidator.predicate("Should return at least the created products")(allResult.pagination.records >= products.length);
  const createdInternalCodes = products.map((p) => p.internal_code);
  TestValidator.predicate("Returned data includes created products")(
    createdInternalCodes.every((code) => allResult.data.some((item) => item.internal_code === code)),
  );

  // 3. FILTERING: By name, by status, by internal_code
  for (const product of createdProducts) {
    // By name
    const byName = await api.functional.productCatalog.products.patch(connection, {
      body: { name: product.name },
    });
    typia.assert<IPageIProductCatalogProduct>(byName);
    TestValidator.predicate("Filter by name: only matching product returned")(
      byName.data.length > 0 && byName.data.every((d) => d.name === product.name),
    );

    // By internal_code
    const byCode = await api.functional.productCatalog.products.patch(connection, {
      body: { internal_code: product.internal_code },
    });
    typia.assert<IPageIProductCatalogProduct>(byCode);
    TestValidator.predicate("Filter by code: only correct product")(
      byCode.data.length > 0 && byCode.data.every((d) => d.internal_code === product.internal_code),
    );

    // By status
    const byStatus = await api.functional.productCatalog.products.patch(connection, {
      body: { status: product.status },
    });
    typia.assert<IPageIProductCatalogProduct>(byStatus);
    TestValidator.predicate("Filter by status: results valid")(
      byStatus.data.length > 0 && byStatus.data.every((d) => d.status === product.status),
    );
  }

  // 4. SEARCH yielding NO results
  const noResults = await api.functional.productCatalog.products.patch(connection, {
    body: { name: "ZZZZzzImpossibleNameZZZ-NotExist" },
  });
  typia.assert<IPageIProductCatalogProduct>(noResults);
  TestValidator.equals("No result - data empty")([] as IProductCatalogProduct[])(noResults.data);
  TestValidator.predicate("No result - records 0")(noResults.pagination.records === 0);

  // 5. INVALID FILTER types (expecting type or http error)
  await TestValidator.httpError("Invalid filter - wrong type")(400)(async () =>
    // @ts-expect-error: Sending number for name
    api.functional.productCatalog.products.patch(connection, { body: { name: 1234 } }),
  );

  // 6. PAGING boundaries
  const firstPage = await api.functional.productCatalog.products.patch(connection, {
    body: { page: 1, limit: 1 },
  });
  typia.assert<IPageIProductCatalogProduct>(firstPage);
  TestValidator.predicate("Paging: returns at most 1")(
    firstPage.data.length === 1 && firstPage.pagination.limit === 1,
  );

  const maxLimit = 1000;
  const largePage = await api.functional.productCatalog.products.patch(connection, {
    body: { page: 1, limit: maxLimit },
  });
  typia.assert<IPageIProductCatalogProduct>(largePage);
  TestValidator.predicate("Max limit accepted")(largePage.pagination.limit === maxLimit);
  TestValidator.predicate("Data doesn't exceed max limit")(largePage.data.length <= maxLimit);

  // Out-of-range page (should return empty or last page)
  const outOfRange = await api.functional.productCatalog.products.patch(connection, {
    body: { page: 9999, limit: 2 },
  });
  typia.assert<IPageIProductCatalogProduct>(outOfRange);
  TestValidator.predicate("Paging: out-of-range page returns empty")(
    outOfRange.data.length === 0 || outOfRange.pagination.current <= outOfRange.pagination.pages,
  );
}

