  const variant2 = await api.functional.productCatalog.productVariants.post(connection, {
    body: {
      product_id: productId2,
      sku: "TESTSKU002",
      price: 4999.99,
      barcode: "BAR0023456789",
      available: false,
    },
  });
  typia.assert<IProductCatalogProductVariant>(variant2);

  // 2. Basic Listing (no filters): Should see both
  const pageAll = await api.functional.productCatalog.productVariants.patch(connection, {
    body: {},
  });
  typia.assert<IPageIProductCatalogProductVariant>(pageAll);
  TestValidator.predicate("pageAll includes created variants")(
    pageAll.data.some((v) => v.id === variant1.id)
  );
  TestValidator.predicate("pageAll includes second variant")(
    pageAll.data.some((v) => v.id === variant2.id)
  );

  // 3. Filter by product_id (only variant1)
  const pageByProduct1 = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { product_id: productId1 },
  });
  typia.assert<IPageIProductCatalogProductVariant>(pageByProduct1);
  TestValidator.predicate("pageByProduct1 only contains variant1")(
    pageByProduct1.data.every((v) => v.product_id === productId1)
  );
  TestValidator.predicate("variant1 is in pageByProduct1")(
    pageByProduct1.data.some((v) => v.id === variant1.id)
  );
  TestValidator.predicate("variant2 is not in pageByProduct1")(
    pageByProduct1.data.every((v) => v.id !== variant2.id)
  );

  // 4. Filter by SKU and barcode (variant1)
  const pageBySKU = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { sku: "TESTSKU001" },
  });
  typia.assert<IPageIProductCatalogProductVariant>(pageBySKU);
  TestValidator.predicate("variant1 in pageBySKU")(
    pageBySKU.data.some((v) => v.sku === "TESTSKU001" && v.id === variant1.id)
  );
  const pageByBarcode = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { barcode: "BAR0012345678" },
  });
  typia.assert<IPageIProductCatalogProductVariant>(pageByBarcode);
  TestValidator.predicate("variant1 in pageByBarcode")(
    pageByBarcode.data.some((v) => v.barcode === "BAR0012345678" && v.id === variant1.id)
  );

  // 5. Filter by 'available' status
  const pageAvailable = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { available: true },
  });
  typia.assert<IPageIProductCatalogProductVariant>(pageAvailable);
  TestValidator.predicate("available only contains enabled variants")(
    pageAvailable.data.every((v) => v.available === true)
  );
  const pageUnavailable = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { available: false },
  });
  typia.assert<IPageIProductCatalogProductVariant>(pageUnavailable);
  TestValidator.predicate("unavailable only contains disabled variants")(
    pageUnavailable.data.every((v) => v.available === false)
  );

  // 6. Pagination test: limit/offset
  const paginated = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { limit: 1, offset: 0 },
  });
  typia.assert<IPageIProductCatalogProductVariant>(paginated);
  TestValidator.predicate("limit=1 returns only 1 record")(
    paginated.data.length === 1
  );
  // Try changing offset
  const paginatedOffset = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { limit: 1, offset: 1 },
  });
  typia.assert<IPageIProductCatalogProductVariant>(paginatedOffset);
  TestValidator.predicate("offset works for variant list")(
    paginatedOffset.data.length === 1 || paginatedOffset.data.length === 0
  );

  // 7. No results filter
  const noResults = await api.functional.productCatalog.productVariants.patch(connection, {
    body: { sku: "NON_EXIST_SKU_XYZ" },
  });
  typia.assert<IPageIProductCatalogProductVariant>(noResults);
  TestValidator.equals("data empty when filter matches nothing")(
    [] as IProductCatalogProductVariant[]
  )(noResults.data);

  // 8. Invalid filter type (should error): string in offset
  await TestValidator.httpError("offset as string should error")()
    (400)
    (async () => {
      // @ts-expect-error: intentionally providing incorrect type
      await api.functional.productCatalog.productVariants.patch(connection, {
        // @ts-ignore
        body: { offset: "WRONGTYPE" },
      });
    });
}

