import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IProductCatalogProduct } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProduct";
import { IProductCatalogProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProductVariant";
import typia from "typia";

export async function test_create_product_variant_with_complete_and_invalid_payloads(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a product to be the parent for the variant
  const productInput: IProductCatalogProduct.ICreate = {
    created_by_user_id: crypto.randomUUID(),
    updated_by_user_id: crypto.randomUUID(),
    name: `Test Product ${Date.now()}`,
    description: "A product for testing variants.",
    internal_code: `TEST-${Date.now()}`,
    status: "active",
  };
  const product = await api.functional.productCatalog.products.post(connection, {
    body: productInput,
  });
  typia.assert<IProductCatalogProduct>(product);
  TestValidator.predicate("product created has id")(() => typeof product.id === "string" && product.id.length > 0);

  // 2. Create product variant (positive case)
  const sku = `SKU-${Date.now()}`;
  const barcode = `BAR-${Math.floor(Math.random() * 1_000_000_000)}`;
  const variantInput: IProductCatalogProductVariant.ICreate = {
    product_id: product.id,
    sku,
    price: 123.45,
    barcode,
    available: true,
  };
  const variant = await api.functional.productCatalog.productVariants.post(connection, {
    body: variantInput,
  });
  typia.assert<IProductCatalogProductVariant>(variant);
  TestValidator.predicate("variant created has id")(() => typeof variant.id === "string" && variant.id.length > 0);
  TestValidator.equals("variant.product_id matches parent product")<string>(variantInput.product_id)(variant.product_id);
  TestValidator.equals("variant.sku matches input")<string>(sku)(variant.sku);

  // 3. Negative Case 1: Missing required fields (e.g., missing sku)
  const incompleteInput: Partial<IProductCatalogProductVariant.ICreate> = {
    product_id: product.id,
    price: 100.00,
    barcode: "MISSING-SKU",
    available: true,
    // sku is missing
  };
  await TestValidator.error("missing SKU should error")(async () => {
    // Force the type to any to bypass TS, as API will reject missing required property
    await api.functional.productCatalog.productVariants.post(connection, {
      body: incompleteInput as any,
    });
  });

  // 4. Negative Case 2: Duplicate SKU (uniqueness enforced for same product)
  await TestValidator.error("duplicate SKU for same product should error")(async () => {
    await api.functional.productCatalog.productVariants.post(connection, {
      body: {
        product_id: product.id,
        sku, // duplicate
        price: 222.22,
        barcode: `BAR-${Math.floor(Math.random() * 1_000_000_000)}`,
        available: false,
      },
    });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IProductCatalogProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProductVariant";
import { IProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductVariant";
import typia, { tags } from "typia";

/**
 * Test updating allowed fields on an existing product variant and audit changes.
 */
export async function test_update_product_variant_and_audit_changes(connection: api.IConnection): Promise<void> {
  // 1. Create a new product variant to update.
  const createInput: IProductCatalogProductVariant.ICreate = {
    product_id: typia.random<string & tags.Format<"uuid">>(),
    sku: `TESTSKU-${Date.now()}`,
    price: 5990,
    barcode: `BARCODE${Date.now()}`,
    available: true
  };
  const created: IProductCatalogProductVariant = await api.functional.productCatalog.productVariants.post(connection, { body: createInput });
  typia.assert(created);
  TestValidator.equals("product_id after create")(createInput.product_id)(created.product_id);

  // 2. Update allowed fields (price, barcode, available)
  const updateInput: IProductVariant.IUpdate = {
    price: 6990,
    barcode: created.barcode + "-UPDATED",
    available: false
  };
  const updated: IProductVariant = await api.functional.productCatalog.productVariants.putById(connection, {
    id: created.id,
    body: updateInput
  });
  typia.assert(updated);
  // 3. Assert that update reflected in fields
  TestValidator.equals("price after update")(updateInput.price)(updated.price);
  TestValidator.equals("barcode after update")(updateInput.barcode)(updated.barcode);
  TestValidator.equals("available after update")(updateInput.available)(updated.available);
  TestValidator.equals("product_id should remain the same")(created.product_id)(updated.product_id);

  // 4. Negative test - invalid data: negative price
  await TestValidator.httpError("update with invalid price should fail")(400)(async () => {
    await api.functional.productCatalog.productVariants.putById(connection, {
      id: created.id,
      body: {
        price: -1000 // Invalid: Negative price
      }
    });
  });

  // 4b. Negative test: empty SKU
  await TestValidator.httpError("update with empty SKU should fail")(400)(async () => {
    await api.functional.productCatalog.productVariants.putById(connection, {
      id: created.id,
      body: {
        sku: ""
      }
    });
  });

  // 5. Negative test - non-existent variant id
  await TestValidator.httpError("update with non-existent id should fail")(404)(async () => {
    await api.functional.productCatalog.productVariants.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(), // Assuming this random id does not exist
      body: {
        price: 9999
      }
    });
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";

import { IProductCatalogProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProductVariant";
import { IProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductVariant";

export async function test_delete_product_variant_and_validate_referential_integrity(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a new product variant
  const createInput: IProductCatalogProductVariant.ICreate = typia.random<IProductCatalogProductVariant.ICreate>();
  const variant = await api.functional.productCatalog.productVariants.post(connection, { body: createInput });
  typia.assert<IProductCatalogProductVariant>(variant);

  // 2. Soft-delete the created product variant
  const deleteResult = await api.functional.productCatalog.productVariants.eraseById(connection, { id: variant.id });
  typia.assert<IProductVariant.IDeleteResult>(deleteResult);
  TestValidator.equals("product variant id matches")(variant.id)(deleteResult.id);
  TestValidator.predicate("deleted_at must be a string")(() => typeof deleteResult.deleted_at === "string");
  TestValidator.predicate("status should be 'deleted' or similar")(
    () => typeof deleteResult.status === "string" && deleteResult.status.length > 0
  );

  // 3. Attempt to delete the same variant again (should fail)
  await TestValidator.httpError("double deletion should fail")(404, 409)(async () =>
    api.functional.productCatalog.productVariants.eraseById(connection, { id: variant.id })
  );

  // 4. Attempt to delete a non-existent (random) variant id (should fail)
  const fakeId: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("delete non-existent variant fails")(404)(async () =>
    api.functional.productCatalog.productVariants.eraseById(connection, { id: fakeId })
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";

import { IProductCategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCategory";
import { IPageIProductCategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIProductCategory";

export async function test_list_product_categories_with_filters_and_hierarchy_check(connection: api.IConnection): Promise<void> {
  // 1. Create root and child categories
  const rootCategory = await api.functional.productCatalog.productCategories.post(connection, {
    body: { name: "Root Category" },
  });
  typia.assert<IProductCategory>(rootCategory);

  const childA = await api.functional.productCatalog.productCategories.post(connection, {
    body: { name: "Child A", parent_id: rootCategory.id },
  });
  const childB = await api.functional.productCatalog.productCategories.post(connection, {
    body: { name: "Child B", parent_id: rootCategory.id },
  });
  typia.assert<IProductCategory>(childA);
  typia.assert<IProductCategory>(childB);

  // 2. List all categories (no filter)
  const resAll = await api.functional.productCatalog.productCategories.patch(connection, { body: {} });
  typia.assert<IPageIProductCategory>(resAll);
  TestValidator.predicate("at least three categories created")(() => resAll.data.length >= 3);

  // 3. Filter with parent_id (should find children only)
  const resChildren = await api.functional.productCatalog.productCategories.patch(connection, {
    body: { parent_id: rootCategory.id },
  });
  typia.assert<IPageIProductCategory>(resChildren);
  TestValidator.equals("children count", undefined)([childA.id, childB.id].sort())(resChildren.data.map(x => x.id).sort());
  for (const ch of resChildren.data) {
    TestValidator.equals("parent_id matches root id")(rootCategory.id)(ch.parent_id!);
  }

  // 4. Filter by name partial match
  const resName = await api.functional.productCatalog.productCategories.patch(connection, {
    body: { name: "Child" },
  });
  typia.assert<IPageIProductCategory>(resName);
  TestValidator.predicate("contains child categories by name filter")(() =>
    resName.data.some(c => c.name === childA.name) && resName.data.some(c => c.name === childB.name)
  );

  // 5. Invalid parameter types (string instead of uuid)
  await TestValidator.error("invalid parent_id type should error")(() =>
    api.functional.productCatalog.productCategories.patch(connection, {
      body: { parent_id: "not-a-uuid" as any },
    })
  );

  // 6. Paging: limit = 1, offset = 1
  const resPaging = await api.functional.productCatalog.productCategories.patch(connection, {
    body: { limit: 1, offset: 1 },
  });
  typia.assert<IPageIProductCategory>(resPaging);
  TestValidator.equals("paging limit=1")(1)(resPaging.data.length);

  // 7. Edge: non-existent parent_id
  await TestValidator.predicate("non-existent parent_id returns empty")(() =>
    api.functional.productCatalog.productCategories.patch(connection, {
      body: { parent_id: "00000000-0000-0000-0000-000000000000" },
    }).then(res => res.data.length === 0)
  );

  // 8. Check parent_id hierarchy and all fields
  for (const c of resAll.data) {
    typia.assert<IProductCategory>(c);
    if (c.parent_id) {
      TestValidator.equals("parent_id correctness")(rootCategory.id)(c.parent_id);
    }
  }

  // 9. Negative: invalid limit/offset
  await TestValidator.error("negative limit should error")(() =>
    api.functional.productCatalog.productCategories.patch(connection, {
      body: { limit: -1 },
    })
  );
  await TestValidator.error("negative offset should error")(() =>
    api.functional.productCatalog.productCategories.patch(connection, {
      body: { offset: -10 },
    })
  );
}

